INSERT INTO `mql_cmt` (`cmt_id`, `ten_cmt`, `cmt`, `product_id`, `productName`, `SĐT`, `email`, `status`, `date_cmt`) VALUES
(14, 'Nghĩa', 'Sen đá thạch bích cánh bướm là loại cây ưa mát, nhưng vẫn cần có ánh sáng, phát triển tốt trong khoảng 20 đến 35 độ C. Độ ẩm trong khoảng 30-70% sen đá đều phát triển tốt.', 20, 'SEN ĐÁ THẠCH BÍCH CÁNH BƯỚM', 2147483647, 'trong@123.com', 0, '2021-05-01 18:31:57'),
(15, 'Tài', '– Cây Tâm phúc cẩm thạch phân nhiều cành nhánh thân nhánh dẻo có nhựa trắng. Thân cây có thể uốn nắn và cắt tỉa được vì chúng rất dẽo dai, Người trồng thướng cắt col tròn hay dạng hình tháp để phù hợp. Một số cây cành nhánh mọc tự nhiện tạo đường nét quyến rũ.\r\n\r\n– Lá cây bàng tam phúc là điểm nổi bậc nhất, lá nhỏ nhưng mọc rất nhiều trên thân nhánh, lá của cây này chỉ bằng lá của cây bàng đài loan đường kính khoảng 1-2cm.\r\n\r\nLá cây tâm phúc hình xoan, hoặc tim ở ngọn. Lá đơn mọc cách, những lá trên đầu ngọn  xòe điều như những cánh hoa, phiến lá màu xanh lục, mép màu trắng, gân trắng, hai mép lá nguyên. Mặt dưới của lá có màu trắng và gân đen. Cây có quả tròn nhỏ màu nâu đỏ, quả mọc trên nách lá.', 20, 'SEN ĐÁ THẠCH BÍCH CÁNH BƯỚM', 18001506, 'trong@123.com', 0, '2021-05-01 18:40:37');

