--
-- Đang đổ dữ liệu cho bảng `mql_product`
--

INSERT INTO `mql_product` (`productId`, `productName`, `catId`, `product_desc`, `price`, `type`, `image`) VALUES
(20, 'SEN ĐÁ THẠCH BÍCH CÁNH BƯỚM', 2, '<p>Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm &ndash; lo&agrave;i c&acirc;y l&agrave;m si&ecirc;u l&ograve;ng biết bao người m&ecirc; c&acirc;y cảnh, n&oacute; kh&ocirc;ng chỉ mang &yacute; nghĩa cho sự gi&agrave;u sang ph&uacute; qu&yacute;, sự chung thủy son sắc. V&igrave; thến&ecirc;n loại c&acirc;y&nbsp;<span>Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm</span>&nbsp;được rất nhiều người mua l&agrave;m qu&agrave; tặng cho nhau để thể hiện tấm ch&acirc;n t&igrave;nh của m&igrave;nh. C&acirc;y nhỏ xinh xắn, rất ph&ugrave; hợp để trang tr&iacute; b&agrave;n l&agrave;m việc, l&agrave;m tiểu cảnh&hellip;&hellip;&hellip;.</p>\r\n<p>C&acirc;y Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm l&agrave; c&acirc;y l&acirc;u năm, &nbsp;c&aacute;c l&aacute; xếp v&ograve;ng xoắn như c&acirc;y thạch b&iacute;ch rất đẹp v&igrave; thế n&oacute; được gọi l&agrave; &nbsp;Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm. Dưới đ&acirc;y l&agrave; một v&agrave;i gợi &yacute; gi&uacute;p bạn chăm s&oacute;c Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm để c&acirc;y sinh trưởng v&agrave; đẹp như &yacute; muốn.</p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-Th%E1%BA%A1ch-B%C3%ADch-C%C3%A1nh-B%C6%B0%E1%BB%9Bm-2.jpg\"><img class=\" wp-image-5358 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-Th%E1%BA%A1ch-B%C3%ADch-C%C3%A1nh-B%C6%B0%E1%BB%9Bm-2.jpg\" alt=\"sen-da-thach-bich-canh-buom-2\" width=\"569\" height=\"427\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Thạch-B&iacute;ch-C&aacute;nh-Bướm-2.jpg\" /></a></p>\r\n<p><span>1. &Aacute;nh s&aacute;ng</span></p>\r\n<p>L&agrave; loại c&acirc;y ưa s&aacute;ng, c&oacute; thể chịu được &aacute;nh nắng trực tiếp v&igrave; thế nếu trồng trong văn ph&ograve;ng hoặc nơi &iacute;t nắng, th&igrave; khoảng 2 ng&agrave;y n&ecirc;n mang c&acirc;y ra phơi nắng khoảng 4 tiếng.</p>\r\n<p>&Aacute;nh s&aacute;ng quyết định m&agrave;u sắc của c&acirc;y, khi c&acirc;y đủ &aacute;nh s&aacute;ng th&igrave; m&agrave;u sắc của l&aacute; c&acirc;y xanh c&acirc;y&nbsp;nh&igrave;n mới đẹp. Gần như tất cả Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm đều cần &aacute;nh s&aacute;ng để ph&aacute;t triển</p>\r\n<p><span>2. Đất trồng</span></p>\r\n<p>C&acirc;y kh&ocirc;ng k&eacute;n đất, ph&ugrave; hợp với nhiều loại đất kh&aacute;c nhau. Đất th&iacute;ch hợp nhất l&agrave; đất tơi xốp dễ tho&aacute;t nước&nbsp;<span>v&agrave; c&oacute; độ m&ugrave;n cao rất ph&ugrave; hợp với sự sinh trưởng v&agrave; ph&aacute;t triển của c&acirc;y. Bạn c&oacute; thể lấy đất vườn trộn với tro, xỉ than đ&atilde; đun rồi nghiền nhỏ trộn với đất, đ&acirc;y l&agrave; loại đất trồng d&agrave;nh cho&nbsp;<span>Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm</span>&nbsp;tốt nhất</span></p>\r\n<p><span>3. Nước</span></p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-th%E1%BA%A1ch-b%C3%ADch-c%C3%A1nh-b%C6%B0%E1%BB%9Bm.jpg\"><img class=\" wp-image-5359 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-th%E1%BA%A1ch-b%C3%ADch-c%C3%A1nh-b%C6%B0%E1%BB%9Bm.jpg\" alt=\"sen-da-thach-bich-canh-buom\" width=\"551\" height=\"413\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-thạch-b&iacute;ch-c&aacute;nh-bướm.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-thạch-b&iacute;ch-c&aacute;nh-bướm.jpg 640w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-thạch-b&iacute;ch-c&aacute;nh-bướm-300x225.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-thạch-b&iacute;ch-c&aacute;nh-bướm-600x450.jpg 600w\" data-lazy-sizes=\"(max-width: 551px) 100vw, 551px\" /></a></p>\r\n<p>Lượng nước phụ thuộc v&agrave;o loại đất trồng, với c&aacute;c loại đất tho&aacute;t nước nhanh th&igrave; 1 ng&agrave;y tưới 2 lần, với đất giữ nước tốt như đất đỏ, c&oacute; thể 3 &ndash; 4 ng&agrave;y tưới 1 lần.&nbsp;Khi đặt c&acirc;y nơi c&oacute; &aacute;nh s&aacute;ng trực tiếp th&igrave; 2 ng&agrave;y tưới nước 1 lần, c&ograve;n khi đặt c&acirc;y trong văn ph&ograve;ng th&igrave; khoảng 3 ng&agrave;y tưới 1 lần.</p>\r\n<p><span>4. Nhiệt độ độ ẩm</span></p>\r\n<p>Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm l&agrave; loại c&acirc;y ưa m&aacute;t, nhưng vẫn cần c&oacute; &aacute;nh s&aacute;ng, ph&aacute;t triển tốt trong khoảng 20 đến 35 độ C. Độ ẩm trong khoảng 30-70% sen đ&aacute; đều ph&aacute;t triển tốt.</p>\r\n<p><span>5. Ph&acirc;n b&oacute;n</span></p>\r\n<p>C&acirc;y&nbsp;<span>Sen đ&aacute; thạch b&iacute;ch c&aacute;nh bướm</span>&nbsp;cũng giống xương rồng kh&ocirc;ng cần qu&aacute; nhiều chất dinh dưỡng,tuy nhi&ecirc;n n&ecirc;n bổ xung một &iacute;t ph&acirc;n b&oacute;n qua l&aacute; h&agrave;ng th&aacute;ng.</p>\r\n<div>&nbsp;</div>\r\n\r\n\r\n<div class=\"fb-comments fb_iframe_widget fb_iframe_widget_fluid_desktop\" data-href=\"https://cayvahoa.net/sen-da-thach-bich-canh-buom/\" data-numposts=\"20\" data-colorscheme=\"light\">&nbsp;</div>', 90000, '1', 'a1939aa734.jpg'),
(21, 'SEN ĐÁ VIỀN HỒNG', 2, '<p>C&acirc;y sen đ&aacute; viền hồng &ndash; lo&agrave;i sen đ&aacute; phổ biến được sử dụng l&agrave;m qu&agrave; tặng bởi n&oacute; c&oacute; &yacute; nghĩa cho một t&igrave;nh y&ecirc;u, t&igrave;nh bạn b&agrave;n chặt, trọn đời vĩnh cửu kh&ocirc;ng bao giờ thay đổi được. C&acirc;y th&iacute;ch hợp d&ugrave;ng l&agrave;m trang tr&iacute; g&oacute;c học tập, b&agrave;n l&agrave;m việc&hellip;&hellip;&hellip;..</p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-Vi%E1%BB%81n-H%E1%BB%93ng-1.jpg\"><img class=\"size-full wp-image-5353 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-Vi%E1%BB%81n-H%E1%BB%93ng-1.jpg\" alt=\"sen-da-vien-hong-1\" width=\"640\" height=\"480\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-1.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-1.jpg 640w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-1-300x225.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-1-600x450.jpg 600w\" data-lazy-sizes=\"(max-width: 640px) 100vw, 640px\" /></a></p>\r\n<p>C&acirc;y cảnh&nbsp;<span>Sen đ&aacute; viền hồng</span>&nbsp;<span>thuộc họ thuốc bỏng, c&acirc;y c&oacute; đặc điểm l&agrave; c&oacute; nhiều c&aacute;nh nhỏ h&igrave;nh như c&aacute;nh hoa sen, c&oacute; th&acirc;n thấp từ 10 &ndash; 20 cm, c&oacute; viền hồng v&agrave; hơi l&otilde;m ở bề mặt sắp xếp xung quanh trục tạo th&agrave;nh b&ocirc;ng hồng nh&igrave;n rất đẹp v&agrave; xinh xắn.&nbsp;</span>Chăm s&oacute;c sen đ&aacute; viền hồng đơn giản, c&aacute;ch chăm s&oacute;c của n&oacute; cũng tương tự như sen đ&aacute; viền đen v&agrave; sen đ&aacute; viền đỏ, dưới đ&acirc;y l&agrave; một v&agrave;i ch&uacute; &yacute; khi chăm s&oacute;c sen đ&aacute; viền hồng.</p>\r\n<p><span>1. &Aacute;nh s&aacute;ng</span></p>\r\n<p>Sen đ&aacute; viền hồng&nbsp;cực kỳ rất th&iacute;ch nắng, khi bạn cung cấp lượng &aacute;nh nắng c&agrave;ng nhiều th&igrave; m&agrave;u đen của viền l&aacute; c&agrave;ng đẹp c&ograve;n khi bạn cung cấp kh&ocirc;ng đủ &aacute;nh s&aacute;ng cho c&acirc;y l&aacute; mờ nhạt dần v&agrave; l&aacute; c&ograve;n rũ xuống gần mặt đất.</p>\r\n<p><span>2. Dinh dưỡng</span></p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-Vi%E1%BB%81n-H%E1%BB%93ng-2.jpg\"><img class=\" wp-image-5354 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-Vi%E1%BB%81n-H%E1%BB%93ng-2.jpg\" alt=\"sen-da-vien-hong-2\" width=\"570\" height=\"570\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-2.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-2.jpg 718w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-2-150x150.jpg 150w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-2-300x300.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-2-600x600.jpg 600w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Viền-Hồng-2-180x180.jpg 180w\" data-lazy-sizes=\"(max-width: 570px) 100vw, 570px\" /></a></p>\r\n<p>Sen đ&aacute; viền hồng kh&ocirc;ng cần qu&aacute; nhiều chất dinh dưỡng. C&aacute;c bạn c&oacute; thể bổ sung th&ecirc;m cho chậu hoa sen đ&aacute; ph&acirc;n b&oacute;n qua l&aacute; h&agrave;ng th&aacute;ng. Để c&acirc;y sinh trưởng v&agrave; ph&aacute;t triển tốt, mỗi năm n&ecirc;n thay đất cho c&acirc;y từ 1-2 lần.</p>\r\n<p><span>3. Nước</span></p>\r\n<p>V&igrave; n&oacute; l&agrave; loại c&oacute; nguồn gốc ở xa mạc n&ecirc;n nước chỉ cần &iacute;t th&ocirc;i l&agrave; c&oacute; đ&atilde; c&oacute; thể sống tốt.. H&agrave;ng tuần sen đ&aacute; viền&nbsp;hồng&nbsp;cần được cung cấp một lần nước tưới, mỗi lần chỉ cần ẩm đất l&agrave; đủ.&nbsp;Khi l&aacute; của&nbsp;<span>Sen đ&aacute; viền hồng</span>&nbsp;căng mọng nghĩa l&agrave; kh&ocirc;ng cần phải tưới nước. Ngược lại, bắt đầu c&oacute; hiện tượng &nbsp;h&eacute;o bạn cần lập tức bổ sung nước cho c&acirc;y. Đ&acirc;y l&agrave; dấu hiệu cực k&igrave; dễ nhận biết cho người chơi sen đ&aacute;.</p>\r\n<p><span>4. Đất trồng v&agrave; s&acirc;u bệnh hại</span></p>\r\n<p>Cần để đất lu&ocirc;n kh&ocirc; tho&aacute;ng, v&agrave; kh&ocirc;ng bị &uacute;ng nước, đất d&agrave;nh cho sen đ&aacute; &nbsp;viền hồng cũng phải tơi xốp th&igrave; c&acirc;y với c&oacute; khả năng ph&aacute;t triển nhanh. Đất trồng th&iacute;ch hợp: đất trồng trộn với tro với tỷ lệ 50:50.&nbsp;<span>Sen đ&aacute; viền hồng</span>&nbsp;tương đối &iacute;t s&acirc;u bệnh, tuy nhi&ecirc;n n&oacute; dễ bị thối nhũn do c&acirc;y bị &uacute;ng nước hoặc nhiễm tr&ugrave;ng vết thương.</p>\r\n<p><span>5. Nh&acirc;n giống</span></p>\r\n<p>C&oacute; thể nh&acirc;n giống bằng c&aacute;ch gi&acirc;m l&aacute; bằng c&aacute;ch lấy l&aacute; đ&atilde; trưởng th&agrave;nh đặt ngang hay cắm nghi&ecirc;ng hỗn hợp đất trồng, giữ cho đất trồng lu&ocirc;n ẩm l&agrave; gốc l&aacute; c&oacute; thể ra rễ v&agrave; mọc th&agrave;nh c&acirc;y mới nhanh ch&oacute;ng</p>\r\n<div>&nbsp;</div>\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"fb-comments fb_iframe_widget fb_iframe_widget_fluid_desktop\" data-href=\"https://cayvahoa.net/sen-da-vien-hong/\" data-numposts=\"20\" data-colorscheme=\"light\">&nbsp;</div>', 65000, '0', '32e3240189.jpg'),
(22, 'BÚT ĐO PH VÀ ĐỘ ẨM CỦA ĐẤT', 3, '<p>Bạn&nbsp;đang t&igrave;m hiểu về c&acirc;y&nbsp;<span><a href=\"https://cayvahoa.net/but-do-ph-va-do-am-cua-dat-takemura/\">b&uacute;t&nbsp;đo pH v&agrave;&nbsp;độ&nbsp;ẩm của&nbsp;đất</a></span>&nbsp;để&nbsp;đảm bảo cho sự ph&aacute;t triển của mỗi loại c&acirc;y trồng n&ocirc;ng nghiệp. Trong n&ocirc;ng nghiệp, người d&acirc;n thường muốn bổ sung kịp thời&nbsp;độ&nbsp;ẩm v&agrave; ph&acirc;n b&oacute;n cho&nbsp;đất nhằm th&uacute;c&nbsp;đẩy sự ph&aacute;t triển của c&acirc;y trồng một c&aacute;ch hiệu&nbsp;quả nhất. Tuy nhi&ecirc;n, nếu&nbsp;đất trồng bị chua, kiềm dư hoặc&nbsp;độ&nbsp;ẩm qu&aacute; cao th&igrave; sẽ l&agrave;m giảm khả năng hấp thu chất dinh dưỡng của c&acirc;y trồng.&nbsp;Để c&oacute;&nbsp;được vụ trồng bội thu th&igrave; ch&uacute;ng ta cần ch&uacute;&nbsp;&yacute; tới mối li&ecirc;n hệ giữa&nbsp;độ&nbsp;ẩm v&agrave;&nbsp;độ chua trong&nbsp;đất trồng th&igrave; b&uacute;t&nbsp;đo&nbsp;độ pH v&agrave;&nbsp;độ&nbsp;ẩm của&nbsp;đất rất quan trọng kh&ocirc;ng thể thiếu&nbsp;đối với c&aacute;c loại c&acirc;y c&ocirc;ng nghiệp như: Hồ ti&ecirc;u, cafe, cao su,&hellip;</p>\r\n<p>**&nbsp;<span><a href=\"https://cayvahoa.net/dung-cu-lam-vuon/\">Dụng cụ l&agrave;m vườn</a>&nbsp;/&nbsp;<a href=\"https://cayvahoa.net/binh-xit-ap-suat-cam-tay/\">b&igrave;nh xịt &aacute;p suất cầm tay</a></span></p>\r\n<p><img class=\"alignnone size-full wp-image-7561     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/may-do-do-am-ph-cu-dat.jpg\" alt=\"may-do-do-am-ph-cu-dat\" width=\"500\" height=\"500\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/may-do-do-am-ph-cu-dat.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/may-do-do-am-ph-cu-dat.jpg 500w, https://cayvahoa.net/wp-content/uploads/may-do-do-am-ph-cu-dat-150x150.jpg 150w, https://cayvahoa.net/wp-content/uploads/may-do-do-am-ph-cu-dat-300x300.jpg 300w, https://cayvahoa.net/wp-content/uploads/may-do-do-am-ph-cu-dat-180x180.jpg 180w\" data-lazy-sizes=\"(max-width: 500px) 100vw, 500px\" /></p>\r\n<p>&nbsp;</p>\r\n<p><span>C&acirc;y b&uacute;t đo pH v&agrave; độ ẩm của đất &ndash; Takemura&nbsp;đ&aacute;p&nbsp;ứng</span></p>\r\n<ul>\r\n<li>Đa số c&aacute;c loại đất trong sản xuất n&ocirc;ng nghiệp c&oacute; gi&aacute; trị pH đất trong khoảng 5-9.</li>\r\n<li>Đất ở c&aacute;c v&ugrave;ng c&oacute; lượng mưa cao n&oacute;i chung đều chua do bị rửa tr&ocirc;i c&aacute;c cation: Ca<span class=\"sup\">++</span>, Mg<span class=\"sup\">++</span>, v.v</li>\r\n<li>pH của đất kh&ocirc;ng phải l&agrave; một gi&aacute; trị cố định, n&oacute; thay đổi theo thời gian.</li>\r\n</ul>\r\n<p><span>Hướng dẫn sử dụng c&acirc;y b&uacute;t&nbsp;đo pH&nbsp;đất v&agrave;&nbsp;độ&nbsp;ẩm &ndash;&nbsp;Takemura</span></p>\r\n<p>L&agrave;m sạch v&ograve;ng kim loại của b&uacute;t&nbsp;đo pH&nbsp;đất v&agrave;&nbsp;độ&nbsp;ẩm (đầu nhọn) bằng giấy nh&aacute;m hoặc vải sạch. Nếu&nbsp;đất trồng qu&aacute; kh&ocirc; cần l&agrave;m&nbsp;ẩm phần&nbsp;đất cần&nbsp;đo v&agrave; chờ khoảng 20-30 ph&uacute;t cho&nbsp;ổn&nbsp;định gi&aacute; trị pH.</p>\r\n<p>+&nbsp;<span>Đo độ ẩm đất:</span>&nbsp;Cắm đầu đo xuống đất sao cho 3 v&ograve;ng kim loại của đầu đo ngập trong đất, nhấn n&uacute;t trắng. Đọc chỉ số đo độ ẩm theo kim chỉ tr&ecirc;n m&agrave;n h&igrave;nh (thang đo b&ecirc;n dưới tương ứng từ 10 &ndash; 80% độ ẩm)</p>\r\n<p>+&nbsp;<span>Đo độ pH:</span>&nbsp;Cắm đầu đo xuống đất sao cho 3 v&ograve;ng kim loại của đầu đo ngập trong đất, nhấn n&uacute;t trắng. Đọc chỉ số đo độ ẩm theo kim chỉ tr&ecirc;n m&agrave;n h&igrave;nh (thang đo b&ecirc;n dưới tương ứng từ 10 &ndash; 80% độ ẩm).</p>\r\n<p><span>Ch&uacute;&nbsp;&yacute;:</span>&nbsp;+ Khi tiến h&agrave;nh do pH th&igrave;&nbsp;đất n&ecirc;n&nbsp;ẩm v&agrave; tơi xốp.</p>\r\n<p>+ N&ecirc;n do nhiều vị tr&iacute; kh&aacute;c nhau tr&ecirc;n&nbsp;đất cần&nbsp;đo&nbsp;để lấy gi&aacute; trị trung b&igrave;nh.</p>\r\n<p>+ Sau khi&nbsp;đo n&ecirc;n lau sạch 3 v&ograve;ng kim loại của&nbsp;đầu&nbsp;đo tr&aacute;nh sự gỉ&nbsp;ố nếu c&oacute; vết gỉ&nbsp;ố th&igrave; n&ecirc;n d&ugrave;ng giấy nh&aacute;m ch&agrave; cho sạch.</p>\r\n<p>+ Kh&ocirc;ng n&ecirc;n để phần tr&ecirc;n của th&acirc;n m&aacute;y tiếp x&uacute;c với nước.</p>\r\n<p><img class=\"alignnone size-full wp-image-7562     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/may-do-ph-takemura.jpg\" alt=\"may-do-ph-takemura\" width=\"1920\" height=\"1080\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/may-do-ph-takemura.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/may-do-ph-takemura.jpg 1920w, https://cayvahoa.net/wp-content/uploads/may-do-ph-takemura-300x169.jpg 300w, https://cayvahoa.net/wp-content/uploads/may-do-ph-takemura-768x432.jpg 768w, https://cayvahoa.net/wp-content/uploads/may-do-ph-takemura-1024x576.jpg 1024w, https://cayvahoa.net/wp-content/uploads/may-do-ph-takemura-600x338.jpg 600w\" data-lazy-sizes=\"(max-width: 1920px) 100vw, 1920px\" /></p>\r\n<p><span>Khuyến nghị pH đất của một số loại c&acirc;y trồng</span></p>\r\n<ul>\r\n<li>Với pH từ 5~6 th&iacute;ch hợp để trồng c&acirc;y cảnh c&oacute; hoa như: hoa hồng, lily, đỗ quy&ecirc;n&hellip;</li>\r\n<li>pH từ 5~6.5 th&iacute;ch hợp trồng một số loại c&acirc;y lấy củ lấy hạt v&agrave; rau như: l&uacute;a, khoai t&acirc;y, bắp (ng&ocirc;), rau m&ugrave;i&hellip;</li>\r\n<li>pH từ 6~8 th&iacute;ch hợp trồng một số loại c&acirc;y: rau dền, c&aacute;c loại đậu lấy hạt h&igrave;nh thận(đậu trắng, đậu đen, đậu xanh&hellip;), rau diếp, h&agrave;nh t&acirc;y, dưa leo, c&agrave; rốt, c&agrave; chua, củ cải, c&agrave; t&iacute;m, cần t&acirc;y, bắp cải, hoa c&uacute;c, hoa mẫu đơn, hoa thủy ti&ecirc;n, hoa tulip, hoa cẩm chướng&hellip;</li>\r\n<li>Khoảng pH từ 7~8 th&iacute;ch hợp trồng một số loại c&acirc;y sau: l&uacute;a mạch, l&uacute;a m&igrave;, đậu h&ograve;a lan, c&acirc;y xa trục thảo, c&acirc;y linh thảo&hellip;</li>\r\n</ul>\r\n<p>Với&nbsp;độ pH qu&aacute; cao hoặc qu&aacute; thấp, ngập&nbsp;&uacute;ng hoặc thiếu nước sẽ l&agrave;m c&acirc;y ngh&egrave;o dinh dưỡng&nbsp;đều kh&ocirc;ng th&iacute;ch hợp cho c&acirc;y trồng sinh trưởng v&agrave; ph&aacute;t triển. Trong thời&nbsp;đại khoa học c&ocirc;ng nghệ ph&aacute;t triển nhiều c&ocirc;ng cụ hỗ trợ cho ng&agrave;nh n&ocirc;ng nghiệp ra&nbsp;đời&nbsp;để b&agrave; con n&ocirc;ng d&acirc;n c&oacute; thể dễ d&agrave;ng kiểm tra ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng&nbsp;đo&nbsp;độ pH&nbsp;đất v&agrave;&nbsp;độ&nbsp;ẩm cũng như mức&nbsp;độ dinh dưỡng của&nbsp;đất&nbsp;để c&oacute; biện ph&aacute;p cải tạo&nbsp;đất, năng suất c&acirc;y trồng.</p>\r\n<p><img class=\"alignnone size-full wp-image-7568     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/19858927_1824447304238236_2010567596_o-3.png\" alt=\"19858927_1824447304238236_2010567596_o\" width=\"546\" height=\"307\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/19858927_1824447304238236_2010567596_o-3.png\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/19858927_1824447304238236_2010567596_o-3.png 546w, https://cayvahoa.net/wp-content/uploads/19858927_1824447304238236_2010567596_o-3-300x169.png 300w\" data-lazy-sizes=\"(max-width: 546px) 100vw, 546px\" /></p>\r\n<p>&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&mdash;&ndash;</p>\r\n<p><img class=\"alignnone size-full wp-image-7569     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/19893586_1824453004237666_1165762945_o-2.png\" alt=\"19893586_1824453004237666_1165762945_o\" width=\"536\" height=\"301\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/19893586_1824453004237666_1165762945_o-2.png\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/19893586_1824453004237666_1165762945_o-2.png 536w, https://cayvahoa.net/wp-content/uploads/19893586_1824453004237666_1165762945_o-2-300x168.png 300w\" data-lazy-sizes=\"(max-width: 536px) 100vw, 536px\" /></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span>Đối tượng th&iacute;ch hợp sở hữu</span>&nbsp;<span>c&acirc;y b&uacute;t&nbsp;đo pH&nbsp;đất v&agrave;&nbsp;độ&nbsp;ẩm &ndash;&nbsp;Takemura</span></p>\r\n<ul>\r\n<li>C&aacute;c kĩ sư n&ocirc;ng học</li>\r\n<li>Chuy&ecirc;n vi&ecirc;n ph&acirc;n b&oacute;n</li>\r\n<li>Nh&agrave; sản xuất</li>\r\n<li>C&aacute;c đại l&yacute; ph&acirc;n phối ph&acirc;n b&oacute;n, ph&acirc;n vi sinh</li>\r\n</ul>\r\n<p>=&gt; Để đưa ra quyết định b&oacute;n ph&acirc;n g&igrave;, trồng c&acirc;y g&igrave; ph&ugrave; hợp, cần t&aacute;c động thế n&agrave;o để tăng pH cho đất.</p>', 1100000, '1', '3ea0d30f89.jpg'),
(23, 'Bình tưới cây Emti Spray 1L', 3, '', 20000, '0', '537a10b9ae.png'),
(24, '﻿SEN ĐÁ VÀNG', 2, '<p>Sen đ&aacute; v&agrave;ng lo&agrave;i sen đ&aacute; được nhắc đến bởi việc n&oacute; mang lại nhiều vinh hoa, gi&agrave;u sang v&agrave; ph&uacute; qu&yacute; cho gia chủ. Với những người kinh doanh bu&ocirc;n b&aacute;n th&igrave; sen đ&aacute; v&agrave;ng rất được ưa chuộng. C&acirc;y ph&ugrave; hợp để b&agrave;n l&agrave;m việc, trang tr&iacute; ph&ograve;ng kh&aacute;ch&hellip;</p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-V%C3%A0ng-1.png\"><img class=\"size-full wp-image-5347 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-V%C3%A0ng-1.png\" alt=\"sen-da-vang-1\" width=\"751\" height=\"673\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-1.png\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-1.png 751w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-1-300x269.png 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-1-600x538.png 600w\" data-lazy-sizes=\"(max-width: 751px) 100vw, 751px\" /></a></p>\r\n<p>Sen đ&aacute; v&agrave;ng c&oacute; đặc điểm nổi bật l&agrave; c&aacute;nh d&agrave;y, mọng nước, c&aacute;nh c&oacute; m&agrave;u v&agrave;ng xanh rất đẹp. Khi nh&igrave;n tổng thể từ tr&ecirc;n xuống dưới n&oacute; giống như một b&ocirc;ng hoa tuyệt đẹp v&agrave; đang hướng mắt nh&igrave;n bạn.&nbsp;<span>Sen đ&aacute; v&agrave;ng</span>&nbsp;c&oacute; thể sống được ở c&aacute;c loại đất kh&aacute;c nhau v&agrave; n&oacute; c&oacute; thể chịu được hạn c&oacute; thể l&ecirc;n đến h&agrave;ng th&aacute;ng.</p>\r\n<p><span>1.&Aacute;nh s&aacute;ng v&agrave; nhiệt độ</span></p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-V%C3%A0ng-2.jpg\"><img class=\"wp-image-5348 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-V%C3%A0ng-2.jpg\" alt=\"sen-da-vang-2\" width=\"530\" height=\"530\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-2.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-2.jpg 960w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-2-150x150.jpg 150w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-2-300x300.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-2-768x768.jpg 768w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-2-600x600.jpg 600w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-2-180x180.jpg 180w\" data-lazy-sizes=\"(max-width: 530px) 100vw, 530px\" /></a></p>\r\n<p>&Aacute;nh s&aacute;ng l&agrave; yếu tố quyết định m&agrave;u sắc của loại sen đ&aacute; v&agrave;ng. Nếu c&acirc;y được tiếp x&uacute;c nhiều với &aacute;nh nắng viền l&aacute; sẽ c&oacute; m&agrave;u v&agrave;ng đậm rất đẹp.Sen đ&aacute; v&agrave;ng ưa &aacute;nh s&aacute;ng buổi sớm, n&ecirc;n tr&aacute;nh những c&aacute;i nắng gắt buổi trưa h&egrave;.&nbsp;Th&iacute;ch hợp với sen đ&aacute; v&agrave;ng nhất l&agrave; nhiệt độ trong khoảng 15 -35 độ C</p>\r\n<p><span>2.Nước</span></p>\r\n<p>Lượng nước tưới d&agrave;nh cho&nbsp;<span>Sen đ&aacute; v&agrave;ng</span>&nbsp;kh&ocirc;ng cần nhiều chỉ cần vừa đủ l&agrave; c&acirc;y đ&atilde; c&oacute; thể ph&aacute;t triển mạnh. Nếu tưới qu&aacute; nhiều c&acirc;y sẽ bị thối nhũn m&agrave; chết, chỉ cần để c&acirc;y trong đất c&oacute; độ ẩm cao trong 4 &ndash; 6 ng&agrave;y li&ecirc;n tiếp th&igrave; c&acirc;y sẽ bị thối gốc v&agrave; rụng l&aacute; dần.</p>\r\n<p><span>C&aacute;ch tưới tốt nhất l&agrave; thẩm thấu ngược, c&aacute;ch tưới như sau: để chậu c&acirc;y v&agrave; bồn, khay nước c&oacute; mực nước bằng 1/3 chiều cao chậu để trong 30 gi&acirc;y&nbsp;th&igrave; nhấc ra lu&ocirc;n sẽ đảm bảo đất kh&ocirc;ng bị ẩm qu&aacute;.</span></p>\r\n<p><span>3.&nbsp;S&acirc;u bệnh hại</span></p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-V%C3%A0ng-3.jpg\"><img class=\"size-full wp-image-5349 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-V%C3%A0ng-3.jpg\" alt=\"sen-da-vang-3\" width=\"640\" height=\"480\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-3.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-3.jpg 640w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-3-300x225.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-V&agrave;ng-3-600x450.jpg 600w\" data-lazy-sizes=\"(max-width: 640px) 100vw, 640px\" /></a></p>\r\n<p>Một số bệnh thường gặp l&agrave; rệp s&aacute;p, đốm l&aacute;&hellip;.. cần mua thuốc về xịt ngay tr&aacute;nh ảnh hưởng đến chất lượng của sen đ&aacute; v&agrave;ng. &nbsp;Bệnh thối nhũn do c&acirc;y bị &uacute;ng nước. Đặc điểm nhận dạng để khắc phục t&igrave;nh trạng n&agrave;y l&agrave; nếu l&aacute; bị quắt kh&ocirc;ng mọng nước nghĩa l&agrave; n&oacute; đang thiếu nước v&agrave; cần phải bổ sung ngay lượng nước cần thiết.</p>\r\n<p><span>4. Đất trồng</span></p>\r\n<p>Loại đất trồng ph&ugrave; hợp để&nbsp;<span>Sen đ&aacute; v&agrave;ng</span>&nbsp;sinh trưởng tốt l&agrave; loại đất tho&aacute;t nước v&agrave; tơi xốp để rễ c&acirc;y c&oacute; thể ph&aacute;t triển một c&aacute;ch dễ d&agrave;ng. Bạn c&oacute; thể trộn đất trồng v&agrave; tro với tỷ lệ 50 : 50 l&agrave; ph&ugrave; hợp với sen đ&aacute; nhất.</p>\r\n<div>&nbsp;</div>\r\n\r\n<div class=\"fb-comments fb_iframe_widget fb_iframe_widget_fluid_desktop\" data-href=\"https://cayvahoa.net/%ef%bb%bfsen-da-vang/\" data-numposts=\"20\" data-colorscheme=\"light\">&nbsp;</div>', 55000, '0', 'aea5d65332.jpg'),
(25, 'SEN ĐÁ TỨ PHƯƠNG', 2, '\r\n<p>Sen đ&aacute; tứ phương loại sen đ&aacute; được coi l&agrave; lo&agrave;i c&acirc;y may mắn, mỗi người n&ecirc;n mua một c&acirc;y cho ri&ecirc;ng m&igrave;nh, n&oacute; mang &yacute; nghĩa d&ugrave; bạn c&oacute; ở bốn phương trời, hay ở bất cứ nơi n&agrave;o bản th&acirc;n bạn cũng sẽ được may mắn. C&acirc;y thường được sử dụng để trang tr&iacute; b&agrave;n l&agrave;m việc, qu&aacute;n c&agrave; ph&ecirc;, b&agrave;n học, tiểu cảnh&hellip;&hellip;</p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-T%E1%BB%A9-Ph%C6%B0%C6%A1ng-1.png\"><img class=\"wp-image-5343 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-T%E1%BB%A9-Ph%C6%B0%C6%A1ng-1.png\" alt=\"sen-da-tu-phuong-1\" width=\"620\" height=\"649\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-1.png\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-1.png 800w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-1-286x300.png 286w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-1-768x804.png 768w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-1-600x629.png 600w\" data-lazy-sizes=\"(max-width: 620px) 100vw, 620px\" /></a></p>\r\n<p>Sen đ&aacute; tứ phương được bắt nguồn từ Mexico, c&oacute; đặc điểm l&agrave; c&aacute;nh chỉ mọc theo đ&uacute;ng 4 hướng x&aacute;c định (v&igrave; thế n&oacute; được gọi l&agrave; sen đ&aacute; tứ phương) v&agrave; được xếp chồng l&ecirc;n nhau, l&aacute; nhẵn b&oacute;ng, thu&ocirc;n d&agrave;i về ph&iacute;a ngọn, đầu l&aacute; thường c&oacute; m&agrave;u đỏ như m&agrave;u lửa.&nbsp;<span>Sen đ&aacute; tứ phương</span>&nbsp;rất dễ sống, c&aacute;ch chăm s&oacute;c kh&ocirc;ng hề phức tạp, chỉ cần bạn nhớ một v&agrave;i ch&uacute; &yacute; về điều kiện nhiệt độ, &aacute;nh s&aacute;ng, nước, đất trồng&hellip;.dưới đ&acirc;y:</p>\r\n<p><span>1. Nước</span></p>\r\n<p>Với c&aacute;c loại sen đ&aacute; kh&aacute;c, bạn kh&ocirc;ng được ph&eacute;p tưới trực tiếp l&ecirc;n c&acirc;y, nhưng với sen đ&aacute; tứ phương bạn c&oacute; thể l&agrave;m điều đ&oacute;.&nbsp;Liều lượng tưới bạn chỉ n&ecirc;n tưới 1 tuần 1 -2 lần.&nbsp;Tưới nước cho ngấm đủ xuống rễ c&acirc;y khoảng &frac34; chậu, tuyệt đối, kh&ocirc;ng để nước đọng lại tr&ecirc;n l&aacute; sen sẽ dễ g&acirc;y &uacute;ng l&aacute;.</p>\r\n<p><span>2. &Aacute;nh s&aacute;ng</span></p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-T%E1%BB%A9-Ph%C6%B0%C6%A1ng-2.jpg\"><img class=\" wp-image-5344 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-T%E1%BB%A9-Ph%C6%B0%C6%A1ng-2.jpg\" alt=\"sen-da-tu-phuong-2\" width=\"607\" height=\"809\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-2.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-2.jpg 720w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-2-225x300.jpg 225w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-2-600x800.jpg 600w\" data-lazy-sizes=\"(max-width: 607px) 100vw, 607px\" /></a></p>\r\n<p>Sen đ&aacute; tứ phương kh&ocirc;ng khắt khe về &aacute;nh s&aacute;ng, c&acirc;y chăm s&oacute;c kh&aacute; dễ d&agrave;ng, chỉ cần c&acirc;y c&oacute; đủ nắng th&igrave; m&agrave;u sắc đẹp v&agrave; xanh tốt hơn, c&ograve;n với điều kiện &aacute;nh s&aacute;ng b&igrave;nh thường cũng kh&ocirc;ng ảnh hưởng nhiều đến sự sinh trưởng v&agrave; ph&aacute;t triển của&nbsp;<span>Sen đ&aacute; tứ phương</span>.</p>\r\n<p><span>3. Đất</span></p>\r\n<p>Với sen đ&aacute; tứ phương cũng như c&aacute;c loại sen đ&aacute; kh&aacute;c, thời gian thay đất cho c&acirc;y l&agrave; 6 th&aacute;ng đến 1 năm.&nbsp;Loại đất trồng th&iacute;ch hợp d&agrave;nh cho sen đ&aacute; tứ phương l&agrave; đất dễ tho&aacute;t nước, tơi xốp, độ m&ugrave;n cao..</p>\r\n<p><span>4. Ph&acirc;n b&oacute;n, s&acirc;u bệnh hại</span></p>\r\n<p><span><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-T%E1%BB%A9-Ph%C6%B0%C6%A1ng-3.jpg\"><img class=\"wp-image-5345 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%90%C3%A1-T%E1%BB%A9-Ph%C6%B0%C6%A1ng-3.jpg\" alt=\"sen-da-tu-phuong-3\" width=\"558\" height=\"390\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-3.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-3.jpg 790w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-3-300x210.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-3-768x537.jpg 768w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-Đ&aacute;-Tứ-Phương-3-600x419.jpg 600w\" data-lazy-sizes=\"(max-width: 558px) 100vw, 558px\" /></a></span></p>\r\n<p>Bổ sung cho c&acirc;y ph&acirc;n b&ograve;, ph&acirc;n tan chậm, ph&acirc;n b&oacute;n qua l&aacute; h&agrave;ng th&aacute;ng gi&uacute;p c&acirc;y sinh trưởng v&agrave; ph&aacute;t triển tốt.&nbsp;C&aacute;c bệnh thường gặp l&agrave; rệp s&aacute;p, đốm l&aacute;&hellip;hay thối nhũng do &uacute;ng nước.</p>\r\n<p><span>5. Nh&acirc;n giống</span></p>\r\n<p>Sử dụng phương ph&aacute;p nh&acirc;n giống bằng l&aacute; hoặc c&agrave;nh. C&agrave;nh gi&acirc;m&nbsp;<span>Sen đ&aacute; tứ phương&nbsp;</span>rất dễ ra rễ, thường chỉ 1-2 tuần sau khi gi&acirc;m, l&agrave; c&agrave;nh đ&atilde; ra rễ v&agrave; c&oacute; thể đem trồng. Đ&acirc;y l&agrave; phương ph&aacute;p nh&acirc;n giống tốt nhất m&agrave; bạn n&ecirc;n sử dụng.</p>\r\n<div>&nbsp;</div>\r\n\r\n<div class=\"fb-comments fb_iframe_widget fb_iframe_widget_fluid_desktop\" data-href=\"https://cayvahoa.net/sen-da-tu-phuong/\" data-numposts=\"20\" data-colorscheme=\"light\">&nbsp;</div>', 55000, '0', '9729a1462c.jpg'),
(26, 'Đất Tribat', 11, '<p>Tribat là hổn hợp <a href=\"https://chaucaygiare.vn/danh-muc/dat-trong-cay/\" rel=\"noopener noreferrer\" target=\"_blank\">đất trồng cây</a> có nguồn gốc hữu cơ giàu dinh dưỡng, được bổ sung đầy đủ và cân đối các thành phần khoáng chất đa, trung, vi lượng và giàu vitamin.</p>\r\n<p><img class=\"size-full wp-image-3724 lazy-load-active\" src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01.jpg\" alt=\"\" width=\"800\" height=\"800\" data-src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01.jpg\" data-srcset=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01.jpg 800w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01-150x150.jpg 150w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01-300x300.jpg 300w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01-768x768.jpg 768w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01-600x600.jpg 600w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01-200x200.jpg 200w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tri-bat-trong-hoa-hong-060420-01-100x100.jpg 100w\" />Hình ảnh: Đất Tribat</p>\r\n<p>Được xử lý khử trùng theo công nghệ tiên tiến của Australia loại trừ triệt để tuyến trùng và các vi sinh vật gây hại nên không gây tác động xấu đến cây trồng và môi trường, giúp cây sinh trưởng và phát triển tốt nhất.</p>\r\n<p>Tribat sử dụng thích hợp với tất cả các loại cây trồng: hoa cây kiểng hoặc gieo ươm cây con…</p>\r\n<p>Tribat có thể dùng để bổ sung và thay thế đất trồng cây đã bạc màu đồng thời có tác dụng cải tạo đất bạc màu do đất Tribat có tạo độ tơi xốp cho đất giúp rễ cây trồng hút các chất dinh dưỡng từ đất một cách dễ dàng hơn.</p>\r\n<h2>Đặc điểm thành phần</h2>\r\n<p>Đất Tribat là loại đất được sản xuất riêng để trồng rau và có thành phần chính gồm: mụn vỏ dừa được xay nhỏ trộn chung với đất nuôi trùn đỏ. Như chúng ta đã biết trùn có khả năng cải thiện đất rất tốt, chúng làm tăng độ phì nhiêu và giúp đất tơi xốp hơn vì thế rễ cây có thể dễ dàng hút được các chất dinh dưỡng có trong chúng.</p>\r\n<p><img class=\"size-full wp-image-3721 lazy-load-active\" src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-060420-01.jpg\" alt=\"\" width=\"800\" height=\"578\" data-src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-060420-01.jpg\" data-srcset=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-060420-01.jpg 800w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-060420-01-300x217.jpg 300w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-060420-01-768x555.jpg 768w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-060420-01-600x434.jpg 600w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-060420-01-200x145.jpg 200w\" />Hình ảnh: Đất Tribat</p>\r\n<p>Trong đất có chứa nhiều các vi sinh vật: nấm men, vi khuẩn hiếm khí Lactobacillus, Actinomycetes,… khi chúng lên men sẽ tạo thành các sản phẩm hữu cơ rất có lợi cho cây. Không những thế đất Tribat còn giàu các chất dinh dưỡng khác: Mg, Fe, Zn, Mn, Cu. Các thành phần cấu tạo khác có trong đất:</p>\r\n<p>+ Hàm lượng mùn: 15%</p>\r\n<p>+ Chất hữu cơ: 25%</p>\r\n<p>+ N tổng số: 1%</p>\r\n<p>+ K20 tổng số: 0.73%</p>\r\n<p>+ P2O5 tổng số: 0.3%</p>\r\n<p>+ CEC: 45meq/100g</p>\r\n<p>+ Các trung, vi lượng gồm Mg, Mn, Zn, B, Cu, Mo, Sắt dạng Chelate.</p>\r\n<p>+ Tỷ lệ còn lại là các hạt cấp hạt khác nhau.</p>\r\n<p>Đất sạch tribat thường được phân ra làm hai loại là loại trồng rau và loại trồng rau mầm. Tuy về thành phần chúng không khác nhau là mấy nhưng so với loại trồng rau thì loại trồng rau mầm được nghiên cứu và làm sạch kĩ hơn vì đặc tính của rau mầm rất nhạy cảm với điều kiện môi trường mất vệ sinh và đất chát</p>\r\n<h2>Cách sử dụng</h2>\r\n<p>Tiến hành dùng hỗn hợp đất sạch tribat giàu dinh dưỡng trồng trong các hộc, chậu cây đã chuẩn bị sẵn. Lấy ra theo số lượng vừa đủ so với nhu cầu của từng loại cây trồng. Sau đó ém nhẹ đất quanh gốc và tưới nước đủ ẩm.</p>\r\n<p>Nên thay thế dần đi loại đất cũ đã bạc màu bằng loại đất tribat giàu chất dinh dưỡng cho cây mau phát triển ở giai đoạn đầu.</p>\r\n<h3>Đất Tribat sạch trồng rau</h3>\r\n<p>Sử dụng với độ dày từ 4 – 6cm làm đất nền cho việc gieo cấy tất cả các loại rau sạch hoặc phục vụ cho việc gieo ươm cây con.</p>\r\n<p>Đối với việc trồng rau sạch tại nhà nên sử dụng 1 bao đất sạch tribat 20dm3 với 2 bao đất phù sa tương đương 10k/bao sử dụng được 2 chậu trồng rau cỡ đại (Dài 67cm – rộng 45cm – cao 16cm) hoặc 4 chậu trồng rau cỡ trung (Dài 67cm – rộng 24cm cao 20cm ). Hoặc sử dụng số lượng ít hoặc nhiều đất sạch Tribat hơn tùy thuộc vào điều kiện.</p>\r\n<p><img class=\"size-full wp-image-3722 lazy-load-active\" src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-trong-rau-060420-01.jpg\" alt=\"\" width=\"800\" height=\"400\" data-src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-trong-rau-060420-01.jpg\" data-srcset=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-trong-rau-060420-01.jpg 800w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-trong-rau-060420-01-300x150.jpg 300w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-trong-rau-060420-01-768x384.jpg 768w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-trong-rau-060420-01-600x300.jpg 600w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-tribat-trong-rau-060420-01-200x100.jpg 200w\" />Hình ảnh: Đất Tribat trồng rau</p>\r\n<h3>Đất Tribat trồng hoa</h3>\r\n<p>Đối với ươm hạt giống hoa nên sử dụng hoàn toàn bằng đất Tribat, trong đất tơi xốp, thoát nước tốt giúp kích thích hạt này mầm đồng thời không làm hạt bị thối do bị úng nước. Đặc biệt rất tốt cho việc ươm lan, cây lan con phát triển đồng đều, tỷ lệ sống cao hơn so với các loại đất khác.</p>\r\n<p>Đối với cây hoa nên sử dụng theo tỷ lệ đất phù sa nhiều hơn so với đất trồng rau. Tỷ lệ hợp lý 1:5 (tức 1 phần đất tribat với 5 bao đất trồng hữu cơ hoặc 1 túi đất tribat 5dm3 – 2,5kg với 1 bao đất phù sa hun trấu 10kg).</p>\r\n<p>Cải tạo đất bạc màu, đất lâu năm: Do hàm lượng mùn và chất hữu cơ chiếm 44% rất lý tưởng cho việc cải tạo, bổ sung độ tơi xốp cho đất.</p>\r\n<h2>Công dụng</h2>\r\n<p>Thích hợp cho các loại cây cảnh, cây bon sai, cây rau, cây hoa các loại.</p>\r\n<p>Dùng cho các loại cây ăn quả, cây lấy củ và các loại cây trồng cạn.</p>\r\n<p>Sử dụng cải tạo đất đã bạc màu, làm phân nền phối trộn với phân vô cơ bón cho các loại cây ăn trái sản xuất theo tiêu chuẩn xuất khẩu.</p>\r\n<h2>Đánh giá</h2>\r\n<h3>Ưu điểm</h3>\r\n<ul>\r\n<li>Khi dùng đất này không cần lo nghĩ tới việc nên bón phân gì cho rau vì thành phần của chúng đã có đầy đủ các chất dinh dưỡng cần thiết.</li>\r\n<li>Được nghiên cứu để phát triển hệ rễ tốt nhất nên bộ rễ của các loại rau sẽ hấp thụ được các chất cần thiết nhiều nhất có thể và phát triển thật chắc khỏe. Bộ rễ phát triển chắc khỏe giúp rau thêm xanh và ngon</li>\r\n<li>Đất sạch Tribat đã được khử trùng sạch sẽ ngăn ngừa mọi mầm mống gây bệnh ảnh hưởng đến chất lượng của sản phẩm.</li>\r\n<li>Chúng hoàn toàn không chứa hóa chất nên không gây độc hại cho người sử dụng cũng như môi trường xung quanh.</li>\r\n<li>Không gây độc hại cho người sử dụng và môi trường</li>\r\n</ul>\r\n<h3>Nhược điểm</h3>\r\n<ul>\r\n<li>Đất Tribat chỉ tốt cho cây ở giai đoạn đầu, các giai đoạn sau cây phát triển kém và bị còi cọc vì qua nhiều lần tưới đất sẽ bị bết dính lại làm nước không thoát ra ngoài được khiến rễ cây bị úng. Cách khắc phục: pha trộn thêm với trấu, tro đen để tạo lại độ tơi xốp cho đất.</li>\r\n<li>Cây rau còi cọc, kém phát triển khi sử dụng đất nhiều lần không cải tạo. Và vì nhược điểm trên nên chúng khiến các loại rau: húng, lá lốt, cà chua, ớt nhanh tàn. Cách khắc phục: pha trộn thêm đất phù sa hoặc đất đỏ badan theo tỷ lệ 50:50.</li>\r\n</ul>\r\n<p><img class=\"size-full wp-image-3723 lazy-load-active\" src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-trong-rau-060420-01.jpg\" alt=\"\" width=\"800\" height=\"533\" data-src=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-trong-rau-060420-01.jpg\" data-srcset=\"https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-trong-rau-060420-01.jpg 800w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-trong-rau-060420-01-300x200.jpg 300w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-trong-rau-060420-01-768x512.jpg 768w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-trong-rau-060420-01-600x400.jpg 600w, https://chaucaygiare.vn/wp-content/uploads/2020/04/dat-trong-rau-060420-01-200x133.jpg 200w\" /></p>\r\n<p>Hình ảnh: Đất sạch trồng rau</p>', 45000, '0', '82637977bd.jpg'),
(27, 'Xương Rồng Kim Hổ Thủy Sinh', 1, '<p>Xương rồng kim hổ l&agrave; một trong những loại c&acirc;y c&oacute; khả năng chịu hạn rất tốt, n&oacute; c&oacute; thể chịu được hạn cả th&aacute;ng trời kh&ocirc;ng cần tưới nước. Một trong những nguy&ecirc;n nh&acirc;n khiến c&acirc;y chết chủ yếu l&agrave; do tưới qu&aacute; nhiều nước khiến th&acirc;n c&acirc;y bị vi khuẩn x&acirc;m nhập v&agrave; l&agrave;m thối xương rồng kim hổ.&nbsp;Rất nhiều người ngạc nhi&ecirc;n khi thấy&nbsp;<strong><a href=\"https://webcaycanh.com/xuong-rong-kim-ho-thuy-sinh/\">xương rồng kim hổ thủy sinh</a>.</strong></p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2016/06/xuong-rong-kim-ho-thuy-sinh.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4919\" src=\"https://webcaycanh.com/wp-content/uploads/2016/06/xuong-rong-kim-ho-thuy-sinh-225x300.jpg\" alt=\"xuong-rong-kim-ho-thuy-sinh\" width=\"225\" height=\"300\" /></a></p>\r\n<p><em>Xương rồng kim hổ thủy sinh</em></p>\r\n<h2>ĐẶC ĐIỂM CỦA XƯƠNG RỒNG KIM HỔ THỦY SINH</h2>\r\n<p>Xương rồng kim hổ c&oacute; nguồn gốc từ xa mạc, nơi c&oacute; kh&iacute; hậu nắng n&oacute;ng k&eacute;o d&agrave;i, ch&iacute;nh v&igrave; thế l&aacute; của lo&agrave;i c&acirc;y n&agrave;y được biến dạng sang thể gai để hạn chế sự tho&aacute;t hơi nước ở c&acirc;y. C&acirc;y thường ra hoa cuối thu.</p>\r\n<p>Chắc c&oacute; nhiều người kh&ocirc;ng khỏi ngạc nhi&ecirc;n khi thấy&nbsp;<strong><a href=\"https://webcaycanh.com/xuong-rong-canh/\" rel=\"noopener\" target=\"_blank\">xương rồng</a></strong>&nbsp;được trồng thủy sinh, thực ra điều n&agrave;y l&agrave; ho&agrave;n to&agrave;n b&igrave;nh thường, thậm ch&iacute; nhờ chuyển sang thủy sinh&nbsp;th&igrave; c&acirc;y gần như kh&ocirc;ng lo bị thối v&agrave; cũng chẳng cần phải lo tưới nước.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2016/06/xuong-rong-kim-ho-thuy-sinh-2.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4920\" src=\"https://webcaycanh.com/wp-content/uploads/2016/06/xuong-rong-kim-ho-thuy-sinh-2-270x300.jpg\" alt=\"xuong-rong-kim-ho-thuy-sinh-2\" width=\"270\" height=\"300\" /></a></p>\r\n<p><em>Sau 2 ng&agrave;y chuyển sang thủy sinh đ&atilde; ra rễ mới</em></p>\r\n<p>C&acirc;y đều được chia ra l&agrave;m 2 phần: phần th&acirc;n v&agrave; phần rễ, phần th&acirc;n th&igrave; t&ugrave;y loại m&agrave; c&oacute; c&aacute;c đặc điểm cấu tạo kh&aacute;c nhau, c&ograve;n phần rễ th&igrave; đều giống nhau c&oacute; rễ ch&iacute;nh v&agrave; rễ phụ rễ ch&iacute;nh c&oacute; nhiệm vụ giữ đất để c&acirc;y kh&ocirc;ng bị đổ c&ograve;n rễ phụ l&agrave; rễ c&oacute; nhiệm vụ h&uacute;t nước v&agrave; kho&aacute;ng chất để đưa n&ecirc;n nu&ocirc;i c&acirc;y. Ch&iacute;nh v&igrave; điều &nbsp;đ&oacute; n&ecirc;n bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m l&agrave; c&acirc;y n&agrave;o cũng c&oacute; thể chuyển sang&nbsp;<strong><a href=\"https://webcaycanh.com/cay-thuy-sinh/\">c&acirc;y thủy sinh</a></strong>&nbsp;v&agrave; sống một c&aacute;ch mạnh khỏe v&igrave; rễ vẫn l&agrave;m nhiệm vụ của rễ thậm ch&iacute; l&agrave; n&oacute; c&ograve;n l&agrave;m tốt hơn so với m&ocirc;i trường đất.</p>\r\n<h2><strong>C&Aacute;CH CHĂM S&Oacute;C XƯƠNG RỒNG KIM HỔ THỦY SINH</strong></h2>\r\n<p>Bạn chỉ cần 7 &ndash; 10 ng&agrave;y&nbsp;thay nước một lần, khi thay nhớ đổ nước đi đổ nước lại nhiều lần để tạo ra nhiều oxi trong nước, v&agrave; nhớ khi đổ nước bản chỉ được ph&eacute;p đổ ngậm phần rẽ của xương rồng kim hổ hoặc 1 nửa phần rễ ở nước, kh&ocirc;ng cho nước tiếp x&uacute;c với th&acirc;n c&acirc;y, kh&ocirc;ng c&acirc;y sẽ bị thối. Bạn c&oacute; thể cho th&ecirc;m một ch&uacute;t dung dịch dinh dưỡng v&agrave;o nước nếu c&oacute; điều kiện gi&uacute;p c&acirc;y ph&aacute;t triển tốt hơn c&ograve;n kh&ocirc;ng c&oacute; cũng kh&ocirc;ng sao.</p>\r\n<p>Đối với &aacute;nh s&aacute;ng th&igrave; bạn chỉ n&ecirc;n cho n&oacute; ra &aacute;nh s&aacute;ng nhẹ buổi s&aacute;ng v&agrave; chiều tối, tr&aacute;nh &aacute;nh s&aacute;ng ngắt buổi trưa c&oacute; thể l&agrave;m n&oacute;ng b&igrave;nh thủy tinh v&agrave; nước dẫn tới chết c&acirc;y.</p>\r\n<div class=\" d-flex mt-2\">\r\n<div class=\"fb-like fb_iframe_widget\" data-href=\"https://webcaycanh.com/xuong-rong-kim-ho-thuy-sinh/\" data-width=\"\" data-layout=\"button_count\" data-action=\"like\" data-size=\"small\" data-share=\"true\">&nbsp;</div>\r\n</div>', 110000, '0', 'd4dc92490a.jpg');
INSERT INTO `mql_product` (`productId`, `productName`, `catId`, `product_desc`, `price`, `type`, `image`) VALUES
(28, 'Xương Rồng Tai Thỏ', 1, '<p>Xương rồng tai thỏ l&agrave; một trong những lo&agrave;i c&acirc;y kh&aacute; phổ biến v&agrave; được nhiều người biết đến, ngo&agrave;i t&aacute;c dụng l&agrave;m cảnh, n&oacute; c&ograve;n rất nhiều t&aacute;c dụng kh&aacute;c như l&agrave;m thuốc, l&agrave;m thức ăn&hellip;Xương rồng tai thỏ c&oacute; dạng phiến, c&acirc;y c&oacute; nhiều gai nhỏ bao phủ, được trồng nhiều ở miền bắc Mexico v&agrave; t&acirc;y nam nước Mỹ. Đ&acirc;y l&agrave; một loại rau xanh quen thuộc của v&ugrave;ng ch&acirc;u Mỹ Latin, đặc biệt l&agrave; ở Mexico. C&acirc;y ra hoa v&agrave; c&oacute; quả ngọt giải kh&aacute;t được, m&ugrave;i vị giống dưa hấu.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-tai-tho-1.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4573\" src=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-tai-tho-1-225x300.jpg\" alt=\"xuong-rong-tai-tho-1\" width=\"225\" height=\"300\" /></a></p>\r\n<p><em>Xương rồng tai thỏ</em></p>\r\n<h2><span id=\"Dac_diem_cay_xuong_rong_tai_tho\">ĐẶC ĐIỂM C&Acirc;Y XƯƠNG RỒNG TAI THỎ</span></h2>\r\n<p>Xương rồng tai thỏ&nbsp;c&oacute; dạng phiến h&igrave;nh ovan, th&acirc;n c&oacute; m&agrave;u xanh, khi c&acirc;y ph&aacute;t triển thường từ một th&acirc;n ch&iacute;nh để ra 2 nh&aacute;nh cũng c&oacute; thể nhiều hơn thường l&agrave; 2, n&ecirc;n n&oacute; c&oacute; t&ecirc;n gọi l&agrave; xương rồng tai thỏ. Tr&ecirc;n mặt th&acirc;n c&oacute; nhiều lớp gai nhỏ xếp th&agrave;nh từng h&agrave;ng ph&acirc;n phối đều xung quanh bề mặt. Nếu ở điều kiện nhiệt độ m&aacute;t, c&acirc;y ph&aacute;t triển to th&igrave; xương rồng tai thỏ cũng c&oacute; thể mọc l&aacute; thay v&igrave; gai. C&acirc;y c&oacute; thể đạt đến chiều cao v&agrave;i m&eacute;t. Hoa thường c&oacute; m&agrave;u v&agrave;ng hoặc m&agrave;u đỏ.</p>\r\n<h2><span id=\"Cach_cham_soc_xuong_rong_tai_tho\">C&Aacute;CH CHĂM S&Oacute;C XƯƠNG RỒNG TAI THỎ</span></h2>\r\n<p><strong><a href=\"https://webcaycanh.com/xuong-rong-tai-tho/\">Xương rồng tai thỏ</a></strong>&nbsp;l&agrave; loại chịu được hạn nhưng kh&ocirc;ng chịu được &uacute;ng nước v&agrave;i ng&agrave;y liền do vậy, loại đất trồng xương rồng tai thỏ phải l&agrave; loại tho&aacute;t nước, kh&ocirc; tho&aacute;ng.&nbsp;Nếu bạn đang trồng c&acirc;y xương rồng để thu hoạch nh&aacute;nh, Lois Garden tin rằng ph&acirc;n chứa h&agrave;m lượng nitrogen (aka Ni-tơ) cao l&agrave; cần thiết hơn cả. Nếu muốn ra hoa hay quả nhiều, mỗi th&aacute;ng b&oacute;n ph&acirc;n với hỗn hợp kh&ocirc;ng c&oacute; nitrogen (0-10-10) nghen.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-tai-tho-2.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4575\" src=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-tai-tho-2-300x203.jpg\" alt=\"xuong-rong-tai-tho-2\" width=\"300\" height=\"203\" /></a></p>\r\n<p><em>Xương rồng tai thỏ ra quả</em></p>\r\n<h3><span id=\"Nuoc\"><strong>NƯỚC</strong></span></h3>\r\n<p><a href=\"https://webcaycanh.com/xuong-rong-canh/\">Xương rồng</a>&nbsp;cần rất &iacute;t nước mỗi tuần bạn chỉ cần tưới cho n&oacute; một lần, nếu trời ẩm nồm th&igrave; bạn kh&ocirc;ng cần phải tưới nước cho lo&agrave;i c&acirc;y mạnh mẽ n&agrave;y.</p>\r\n<h3><span id=\"Anh_sang\"><strong>&Aacute;NH S&Aacute;NG</strong></span></h3>\r\n<p>&Aacute;nh s&aacute;ng rất cần đối với loại&nbsp;<strong>xương rồng tai thỏ</strong>, bạn c&oacute; thể để c&acirc;y ra nơi c&oacute; &aacute;nh nắng trực tiếp, hoặc nếu trong văn ph&ograve;ng th&igrave; 2 -3 ng&agrave;y h&atilde;y cho n&oacute; tắm nắng một lần để đảm bảo sự sinh trưởng v&agrave; ph&aacute;t triển cho c&acirc;y.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-tai-tho-4.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-5165\" src=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-tai-tho-4-225x300.jpg\" alt=\"xuong-rong-tai-tho-4\" width=\"225\" height=\"300\" /></a></p>\r\n<p><em>C&acirc;y xương rồng tai thỏ</em></p>\r\n<h3><span id=\"Nhiet_do\"><strong>NHIỆT ĐỘ</strong></span></h3>\r\n<p>Xương rồng tai thỏ l&agrave; loại c&acirc;y c&oacute; thể chịu được điều kiện khắc nhiệt nhất trong c&aacute;c lo&agrave;i c&acirc;y kể cả lạnh, hoặc n&oacute;ng.&nbsp;Khả năng chịu đựng trong khoảng nhiệt độ từ 10&deg;C &ndash; 50&deg;C. Tuy nhi&ecirc;n, nhiệt độ th&iacute;ch hợp để c&acirc;y ph&aacute;t triển v&agrave;o khoảng 15&deg;C &ndash; 28&deg;C. Ở Nhiệt độ qu&aacute; thấp&nbsp;hoặc qu&aacute; lớn&nbsp;c&oacute; thể l&agrave;m cho c&acirc;y ngừng ph&aacute;t triển v&agrave; suy yếu.</p>\r\n<h3><span id=\"Dinh_duong\">DINH DƯỠNG</span></h3>\r\n<p>Bản chất l&agrave; c&acirc;y c&oacute; thể sống trong điều kiện ngh&egrave;o n&agrave;n về chất dinh dưỡng, nước nhưng nếu bạn muốn c&acirc;y ph&aacute;t triển nhanh mọng nước, khỏe đẹp sinh trưởng nhiều nh&aacute;nh th&igrave; vẫn c&oacute; thể cung cấp dinh dưỡng cho đất. Trong m&ugrave;a&nbsp;<strong>c&acirc;y xương rồng tai thỏ</strong>&nbsp;ph&aacute;t triển bạn c&oacute; thể b&oacute;n th&ecirc;m đạm ( N ) để gi&uacute;p sự tăng trưởng th&acirc;n, chất potassium (P) cho sự ph&aacute;t triển của hoa v&agrave; tr&aacute;i v&agrave; chất phosphorus (P) cho sự ph&aacute;t triển bộ rễ. Ngo&agrave;i ra, c&acirc;y cũng cần một số chất vi lượng kh&aacute;c. Nhưng tr&aacute;nh b&oacute;n l&uacute;c trời nắng n&oacute;ng hoặc l&uacute;c thời tiết oi bức c&oacute; thể dẫn đến bị sốc khiến c&acirc;y thối dễ v&agrave; chết.</p>\r\n<p>( Lưu &yacute;: Shop chỉ b&aacute;n c&acirc;y nhỏ để b&aacute;n kh&ocirc;ng b&aacute;n những c&acirc;y to để l&agrave;m thuốc&nbsp;v&agrave; l&agrave;m thực phẩm )</p>\r\n<h2><span id=\"Xuong_rong_tai_tho_co_2_loai\">XƯƠNG RỒNG TAI THỎ C&Oacute; 2 LOẠI:</span></h2>\r\n<p>Loại Nhỏ 1 c&acirc;y đơn:&nbsp;<span><strong>30.000 VNĐ</strong></span></p>\r\n<p>Loại Bụi gồm nhiều c&acirc;y:&nbsp;<span><strong>95.000 VNĐ</strong></span></p>\r\n<div class=\" d-flex mt-2\">\r\n<div class=\"fb-like fb_iframe_widget\" data-href=\"https://webcaycanh.com/xuong-rong-tai-tho/\" data-width=\"\" data-layout=\"button_count\" data-action=\"like\" data-size=\"small\" data-share=\"true\">&nbsp;</div>\r\n</div>\r\n<p><em><br /></em></p>\r\n<div><em><br /></em></div>\r\n<div id=\"toc_container\" class=\"no_bullets\">&nbsp;</div>', 29000, '0', 'b3b0d4c796.jpg'),
(29, 'Bộ dụng cụ làm vườn mini', 3, '', 20000, '0', 'fee1732ea9.jpeg'),
(31, 'CHẬU ĐẤT NUNG TRỒNG SEN ĐÁ', 12, '<p>Loại chậu đất nung n&agrave;y c&oacute; k&iacute;ch thước nhỏ từ 5&times;5, 7&times;7 đến 9&times;9 ph&ugrave; hợp cho nhiều loại sen đ&aacute;, m&agrave;u sắc đa dạng phong ph&uacute;</p>\r\n<p><img class=\"alignnone wp-image-345     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung.jpg\" alt=\"chau-dat-nung\" width=\"500\" height=\"334\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung.jpg 490w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-300x200.jpg 300w\" data-lazy-sizes=\"(max-width: 500px) 100vw, 500px\" />&nbsp;<img class=\"alignnone wp-image-346     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-9-9-300x224.jpg\" alt=\"chau-dat-nung-co-9-9\" width=\"500\" height=\"374\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-9-9-300x224.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-9-9-300x224.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-9-9.jpg 768w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-9-9-600x448.jpg 600w\" data-lazy-sizes=\"(max-width: 500px) 100vw, 500px\" />&nbsp;<img class=\"alignnone wp-image-347     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-nho.jpg\" alt=\"chau-dat-nung-co-nho\" width=\"500\" height=\"329\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-nho.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-nho.jpg 4828w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-nho-300x198.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-nho-768x506.jpg 768w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-nho-1024x674.jpg 1024w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-co-nho-600x395.jpg 600w\" data-lazy-sizes=\"(max-width: 500px) 100vw, 500px\" />&nbsp;<img class=\"alignnone wp-image-348     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-trong-sen-da.jpg\" alt=\"chau-dat-nung-trong-sen-da\" width=\"500\" height=\"667\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-trong-sen-da.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-trong-sen-da.jpg 720w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-trong-sen-da-225x300.jpg 225w, https://cayvahoa.net/wp-content/uploads/2016/03/chau-dat-nung-trong-sen-da-600x800.jpg 600w\" data-lazy-sizes=\"(max-width: 500px) 100vw, 500px\" /></p>', 20000, '0', 'b16db906be.jpg'),
(32, 'Xương Rồng Trứng Chim', 1, '<p>Xương rồng trứng chim c&oacute; một lớp gai nhỏ m&agrave;u trắng bao phủ kh&aacute; k&iacute;n để bảo vệ c&acirc;y. Thoạt đầu nếu bạn nh&igrave;n từ xa c&oacute; thể n&oacute; khiến bạn nghĩ đ&oacute; l&agrave; những quả trứng nhỏ, rất ngộ nghĩnh v&agrave; đ&aacute;ng y&ecirc;u, c&oacute; thể ch&iacute;nh v&igrave; l&yacute; do đ&oacute; m&agrave; lo&agrave;i xương rồng n&agrave;y c&oacute; t&ecirc;n gọi l&agrave;&nbsp;<strong><a href=\"https://webcaycanh.com/xuong-rong-trung-chim/\">xương rồng trứng chim</a></strong>. C&acirc;y ra hoa nhỏ c&oacute; m&agrave;u trắng hoặc đỏ.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-trung-chim-3.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4553\" src=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-trung-chim-3-300x225.jpg\" alt=\"xuong-rong-trung-chim-3\" width=\"300\" height=\"225\" /></a></p>\r\n<p><em>Xương rồng trứng chim</em></p>\r\n<div><em><br /></em></div>\r\n<div id=\"toc_container\" class=\"no_bullets\">&nbsp;\r\n<h2><span id=\"Cach_cham_soc_xuong_rong_trung_chim\">C&Aacute;CH CHĂM S&Oacute;C XƯƠNG RỒNG TRỨNG CHIM</span></h2>\r\n<p>Hầu hết c&aacute;c bạn cũng đ&atilde; biết xương rồng l&agrave; lo&agrave;i c&acirc;y rất ki&ecirc;n cường, n&oacute; c&oacute; khả năng chịu được nắng n&oacute;ng v&agrave; kh&ocirc; hạn k&eacute;o d&agrave;i. Xương rồng trứng chim cũng vậy, th&acirc;n c&acirc;y mọng nước v&agrave; c&oacute; gai bao phủ, như tấm l&aacute; chắn, khiến sự khi&ecirc;n cường của n&oacute; c&agrave;ng được tăng l&ecirc;n.</p>\r\n<h3><span id=\"Anh_sang\"><strong>&Aacute;NH S&Aacute;NG</strong></span></h3>\r\n<p><a href=\"https://webcaycanh.com/xuong-rong-canh/\">Xương rồng</a>&nbsp;n&agrave;o cũng vậy n&oacute; rất th&iacute;ch &aacute;nh s&aacute;ng, &aacute;nh s&aacute;ng gi&uacute;p c&acirc;y c&oacute; m&agrave;u đẹp hơn, v&agrave; ph&aacute;t triển tốt hơn. N&oacute; c&oacute; thể chịu được c&aacute;c &aacute;nh nắng trực tiếp v&agrave; gat gắt m&ugrave;a h&egrave;. Nhưng đối với c&aacute;c c&acirc;y con th&igrave; bạn vẫn l&ecirc;n để ch&uacute;ng ở nơi c&oacute; m&aacute;i che. Nếu bạn để trong văn ph&ograve;ng th&igrave; 2 -3 ng&agrave;y n&ecirc;n cho c&acirc;y tắm nắng 1 lần để đam bảo được sự sinh trưởng v&agrave; ph&aacute;t triển.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-trung-chim-1.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4551\" src=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-trung-chim-1-300x183.jpg\" alt=\"xuong-rong-trung-chim-1\" width=\"300\" height=\"183\" /></a></p>\r\n<p><em>Xương rồng trứng chim ra hoa</em></p>\r\n<h3><span id=\"Nuoc\"><strong>NƯỚC</strong></span></h3>\r\n<p>T&ugrave;y theo thời tiết m&agrave; bạn tưới nước cho c&acirc;y, thường th&igrave; l&agrave; 1 tuần một lần, nếu thời tiết ấm c&oacute; khi cả th&aacute;ng bạn cũng đừng n&ecirc;n tưới nước cho n&oacute;. Xương rồng trứng chim c&oacute; thể chịu được kh&ocirc; hạn nhưng &uacute;ng nước v&agrave;i ng&agrave;y l&agrave; n&oacute; chết ngay.</p>\r\n<h3><span id=\"Dat\"><strong>ĐẤT</strong></span></h3>\r\n<p>Lựa chọn loại đất c&oacute; khả năng tho&aacute;t nước tốt v&agrave; tơi xốp, để rễ xương rồng c&oacute; thể ph&aacute;t triển mạnh v&agrave; kh&ocirc;ng bị &uacute;ng nước.</p>\r\n<h3><span id=\"Nhan_giong\"><strong>NH&Acirc;N GIỐNG</strong></span></h3>\r\n<p>Bạn c&oacute; thể lấy c&aacute;i nh&aacute;nh nhỏ của c&acirc;y gi&acirc;m xuống đất để ph&aacute;t triển th&agrave;nh c&acirc;y con.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-trung-chim-6.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4589\" src=\"https://webcaycanh.com/wp-content/uploads/2015/11/xuong-rong-trung-chim-6-300x300.jpg\" alt=\"xuong-rong-trung-chim-6\" width=\"300\" height=\"300\" /></a></p>\r\n<h2><span id=\"Co_2loai_xuong_rong_trung_chim\">C&Oacute; 2&nbsp;LOẠI XƯƠNG RỒNG TRỨNG CHIM</span></h2>\r\n<p>Loại nhỏ trắng v&agrave; loại xanh. Loại xương rồng trứng chim xanh sẽ c&oacute; hoa đỏ, xương rồng trứng chim c&oacute; m&agrave;u xanh nhạt hơn th&igrave; c&oacute; hoa m&agrave;u v&agrave;ng.</p>\r\n<p>Sản phẩm c&oacute; gi&aacute;&nbsp;<span>30.000 VNĐ</span></p>\r\n<div class=\" d-flex mt-2\">\r\n<div class=\"fb-like fb_iframe_widget\" data-href=\"https://webcaycanh.com/xuong-rong-trung-chim/\" data-width=\"\" data-layout=\"button_count\" data-action=\"like\" data-size=\"small\" data-share=\"true\">&nbsp;</div>\r\n</div>\r\n</div>', 29000, '0', '912afbe5cd.jpg'),
(33, 'CHẬU SỨ NHỎ ĐẸP', 12, '\r\n<p><span>Trồng c&acirc;y cảnh trang tr&iacute; nội thất lu&ocirc;n đ&ograve;i hỏi những kỹ thuật trồng tỉ mỉ để đem lại vẻ đẹp ho&agrave;n thiện cho c&acirc;y cảnh. Lựa chọn h&igrave;nh d&aacute;ng, k&iacute;ch thước, vị tr&iacute; đặt c&acirc;y trong chậu cần c&acirc;n đối v&agrave; đẹp mắt, tuy nhi&ecirc;n cũng cần quan t&acirc;m đến khả năng th&iacute;ch nghi của c&acirc;y cảnh với loại chậu được chọn.&nbsp;</span><span>Chậu sứ nhỏ đẹp</span><span>&nbsp;cũng được nhiều người chơi c&acirc;y cảnh lựa chọn.</span></p>\r\n<ul>\r\n<li><em><span><a href=\"https://cayvahoa.net/lua-chon-chau-dat-nung-mi-ni/\">T&igrave;m hiểu về chậu đất nung mini</a></span></em></li>\r\n</ul>\r\n<p>&nbsp;<a href=\"https://cayvahoa.net/wp-content/uploads/2016/04/20130917112629.jpg\" rel=\"attachment wp-att-1288\"><img class=\"aligncenter wp-image-1288 size-full     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/04/20130917112629.jpg\" alt=\"chậu sứ nhỏ đẹp1\" width=\"640\" height=\"427\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/04/20130917112629.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/04/20130917112629.jpg 640w, https://cayvahoa.net/wp-content/uploads/2016/04/20130917112629-300x200.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/04/20130917112629-600x400.jpg 600w\" data-lazy-sizes=\"(max-width: 640px) 100vw, 640px\" /></a></p>\r\n<p><span>Đối với c&acirc;y cảnh chỉ c&oacute; 1 th&acirc;n đơn , nếu trồng trong&nbsp;</span><span>chậu sứ nhỏ đẹp</span><span>&nbsp;h&igrave;nh chữ nhật hay bầu dục th&igrave; n&ecirc;n trồng c&acirc;y hơi lệch sang một b&ecirc;n. Nếu trồng ở chậu tr&ograve;n, vu&ocirc;ng hay lục gi&aacute;c, th&igrave; trồng c&acirc;y ở ngay ch&iacute;nh giữa.</span></p>\r\n<p><span>Nếu c&acirc;y cảnh c&oacute; th&acirc;n thẳng, t&aacute;n l&aacute; tr&ograve;n đều th&igrave; trồng c&acirc;y l&ugrave;i về ph&iacute;a sau, th&acirc;n hơi nghi&ecirc;ng về ph&iacute;a trước. Nếu t&aacute;n c&acirc;y lệch về một ph&iacute;a th&igrave; n&ecirc;n đặt c&acirc;y nghi&ecirc;ng về ph&iacute;a dối diện .</span></p>\r\n<p><span>Nếu c&acirc;y cảnh c&oacute; th&acirc;n nghi&ecirc;ng hay cong queo th&igrave; th&acirc;n nghi&ecirc;ng về ph&iacute;a n&agrave;o, sẽ đặt c&acirc;y hơi lệch về ph&iacute;a đối diện v&agrave; nghi&ecirc;ng về ph&iacute;a trước.</span></p>\r\n<p><span>Đối với nh&oacute;m c&acirc;y, thường số th&acirc;n c&acirc;y lẻ n&ecirc;n đặt kh&oacute;m c&acirc;y hơi lệch về b&ecirc;n phải hoặc b&ecirc;n tr&aacute;i trong chậu d&aacute;ng bầu dục.</span></p>\r\n<p><span>Ngo&agrave;i ra, khi trồng nhiều c&acirc;y cảnh trong c&ugrave;ng một chậu cần quan t&acirc;m đến kỹ thuật phối cảnh cơ bản như sau :</span></p>\r\n<p><span>&ndash; Nh&oacute;m ba c&acirc;y được trồng th&agrave;nh một tam gi&aacute;c lệch</span></p>\r\n<p><span>&ndash; Nh&oacute;m năm c&acirc;y được trồng th&agrave;nh h&igrave;nh tam gi&aacute;c k&eacute;p</span></p>\r\n<p><span>&ndash; Nh&oacute;m ch&iacute;n c&acirc;y được trồng th&agrave;nh h&igrave;nh tam gi&aacute;c trong lục gi&aacute;c</span></p>\r\n<p><img class=\"aligncenter wp-image-1289 size-full     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/04/larger_dey1432009695.jpg\" alt=\"chậu sứ nhỏ đẹp\" width=\"600\" height=\"313\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/04/larger_dey1432009695.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/04/larger_dey1432009695.jpg 600w, https://cayvahoa.net/wp-content/uploads/2016/04/larger_dey1432009695-300x157.jpg 300w\" data-lazy-sizes=\"(max-width: 600px) 100vw, 600px\" /></p>\r\n<p><span>Nhiều c&acirc;y cảnh c&oacute; số c&acirc;y kh&ocirc;ng tu&acirc;n theo một h&igrave;nh thức nhất định v&agrave; nếu thi&ecirc;n về ph&iacute;a n&agrave;o th&igrave; n&ecirc;n trồng d&agrave;y về ph&iacute;a đ&oacute;.</span></p>\r\n<p><span>Ngo&agrave;i vị tr&iacute; trồng c&acirc;y trong chậu, k&iacute;ch thước của c&acirc;y cảnh cũng phải h&agrave;i h&ograve;a với độ lớn của chậu. Điểm cần ch&uacute; &yacute; l&agrave; chiều cao của c&acirc;y cảnh v&agrave; bề rộng, độ d&agrave;y của t&aacute;n l&aacute;. Th&ocirc;ng thường th&acirc;n c&acirc;y cảnh c&agrave;ng to th&igrave; chậu c&agrave;ng cần phải s&acirc;u, rộng. C&acirc;y cảnh c&oacute; th&acirc;n to, mập, nhưng thấp th&igrave; chậu kh&ocirc;ng cần s&acirc;u lắm, để g&acirc;y ấn tượng mạnh về kh&ocirc;ng gian v&agrave; cự ly.</span></p>\r\n<p><img class=\"aligncenter wp-image-1290 size-full     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/04/mau-chau-hoa-de-ban-cong-so-xinh-xan-co-gia-duoi-200-nghin-dong.jpg\" alt=\"chậu sứ nhỏ đẹp3\" width=\"600\" height=\"384\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/04/mau-chau-hoa-de-ban-cong-so-xinh-xan-co-gia-duoi-200-nghin-dong.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/04/mau-chau-hoa-de-ban-cong-so-xinh-xan-co-gia-duoi-200-nghin-dong.jpg 600w, https://cayvahoa.net/wp-content/uploads/2016/04/mau-chau-hoa-de-ban-cong-so-xinh-xan-co-gia-duoi-200-nghin-dong-300x192.jpg 300w\" data-lazy-sizes=\"(max-width: 600px) 100vw, 600px\" /></p>\r\n<p><span>Th&acirc;n c&acirc;y mảnh mai đường k&iacute;nh nhỏ lại th&iacute;ch hợp với chậu sử nhỏ đẹp, n&ocirc;ng, miệng rộng, để kh&ocirc;ng l&agrave;m nặng nề th&ecirc;m tổng thể. Bề s&acirc;u của chậu phải bằng hoặc lớn hơn đường k&iacute;nh gốc c&acirc;y.</span></p>\r\n<p><span>Chậu<em><span><a href=\"https://cayvahoa.net/\">&nbsp;c&acirc;y v&agrave; hoa</a></span></em>&nbsp;kh&ocirc;ng chỉ t&ocirc;n hết vẻ đẹp của c&acirc;y cảnh, m&agrave; c&ograve;n l&agrave; nơi chứa chất dinh dưỡng cần thiết để c&acirc;y sinh trưởng v&agrave; ph&aacute;t triển. Do đ&oacute;</span><span>&nbsp;chậu sứ nhỏ đẹp</span><span>&nbsp;v&agrave; n&ocirc;ng chỉ để trồng c&aacute;c c&acirc;y c&oacute; t&aacute;n c&acirc;y nhỏ, bộ rễ rất &iacute;t ph&aacute;t triển, ngược lại c&acirc;y c&oacute; t&aacute;n lớn, bộ rễ lớn th&igrave; cần chậu lớn s&acirc;u, vừa tạo thế c&acirc;n bằng, vừa c&oacute; đủ chất dinh dưỡng cho c&acirc;y sống b&igrave;nh thường.</span></p>\r\n<div><span><br /></span></div>\r\n\r\n<div class=\"fb-comments fb_iframe_widget fb_iframe_widget_fluid_desktop\" data-href=\"https://cayvahoa.net/chau-su-nho-dep/\" data-numposts=\"20\" data-colorscheme=\"light\">&nbsp;</div>', 20000, '0', 'c40e934720.jpg'),
(34, 'Bậc Cầu', 4, '<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/bac-cau-phu-kien-terrarium-9x-garden-6.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/bac-cau-phu-kien-terrarium-9x-garden-2.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/bac-cau-phu-kien-terrarium-9x-garden-1-600x401.jpg\" alt=\"\" /></p>', 15000, '0', '91a09d6eb3.jpg'),
(35, 'Xương rồng kim hổ', 1, '<p>C&acirc;y xương rồng kim hổ thuộc họ: Xương rồng. T&ecirc;n khoa học: Echinocactus grusonii. Kim hổ gai trắng, hoặc nhiều gai. Nguồn gốc từ v&ugrave;ng sa mạc Mehico. Được ph&acirc;n bố tại c&aacute;c v&ugrave;ng sa mạc nhiệt đới kh&ocirc; n&oacute;ng. L&agrave; một trong những loại xương rồng c&oacute; khả năng ph&aacute;t triển bề ngang lớn nhất trong c&aacute;c lo&agrave;i xương rồng. C&acirc;y&nbsp;<strong>xương rồng kim hổ</strong>&nbsp;lớn ph&ugrave; hợp để trước cổng, trước cửa đề trừ t&agrave;, c&acirc;y nhỏ c&oacute; thể d&ugrave;ng để trang tr&iacute;, để b&agrave;n m&aacute;y t&iacute;nh để h&uacute;t bức xạ.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2016/01/xuong-rong-kim-ho.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4724\" src=\"https://webcaycanh.com/wp-content/uploads/2016/01/xuong-rong-kim-ho-300x225.jpg\" alt=\"xuong-rong-kim-ho\" width=\"300\" height=\"225\" /></a></p>\r\n<div>&nbsp;</div>\r\n<div id=\"toc_container\" class=\"no_bullets\">&nbsp;\r\n<h2><span id=\"Dac_diem_cua_xuong_rong_kim_ho\">ĐẶC ĐIỂM CỦA XƯƠNG RỒNG KIM HỔ</span></h2>\r\n<p><strong><a href=\"https://webcaycanh.com/xuong-rong-kim-ho/\"><em>C&acirc;y xương rồng kim hổ</em></a></strong>&nbsp;l&agrave; loại thực vật th&acirc;n thịt c&oacute; gai l&acirc;u năm. Th&acirc;n c&acirc;y c&oacute; m&agrave;u xanh sẫm, h&igrave;nh cầu, c&oacute; g&oacute;c cạnh với m&agrave;u l&ocirc;ng v&agrave;ng v&agrave; gai dẹt m&agrave;u v&agrave;ng kim, gai nhiều ở phần đỉnh c&acirc;y. Hoa m&agrave;u v&agrave;ng, mọc từ đỉnh th&acirc;n c&acirc;y. Thường ra hoa v&agrave;o th&aacute;ng 6 &ndash; 10. M&agrave;ng bao c&oacute; c&aacute;c vẩy v&agrave; l&ocirc;ng tơ, hạt m&agrave;u đen v&agrave; b&oacute;ng nhẵn. Lo&agrave;i thường thấy l&agrave; kim hổ nhiều gai, kim hổ gai trắng v&agrave; l&otilde;a hổ.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2016/01/xuong-rong-kim-ho-3.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4727\" src=\"https://webcaycanh.com/wp-content/uploads/2016/01/xuong-rong-kim-ho-3-300x282.jpg\" alt=\"xuong-rong-kim-ho-ra-hoa\" width=\"300\" height=\"282\" /></a></p>\r\n<p>Xương rồng kim hổ ra hoa</p>\r\n<h2><span id=\"Cach_cham_soc_suong_rong_kim_ho\">C&Aacute;CH CHĂM S&Oacute;C SƯƠNG RỒNG KIM HỔ</span></h2>\r\n<p>L&agrave; lo&agrave;i c&acirc;y c&oacute; nguồn gốc từ xa mạc n&ecirc;n xương rồng kim hổ rất dễ chăm s&oacute;c v&agrave; c&oacute; khả năng th&iacute;ch nghi tốt, c&oacute; thể trồng trong nh&agrave; lẫn ngo&agrave;i trời, c&acirc;y chịu được hạn, nhưng kh&ocirc;ng chịu được ẩm qu&aacute; l&acirc;u.</p>\r\n<h3><span id=\"Dat_trong\">ĐẤT TRỒNG</span></h3>\r\n<p>V&igrave; kh&ocirc;ng chịu được ẩm l&acirc;u, do vậy loại&nbsp;<strong>đất tốt nhất với xương rồng kim hổ</strong>&nbsp;l&agrave; loại c&oacute; khả năng tho&aacute;t nước tốt m&agrave; vẫn c&oacute; nhiều dinh dưỡng, th&iacute;ch&nbsp;hợp với đất trồng pha c&aacute;t c&oacute; chứa chất v&ocirc;i, m&agrave;u mỡ.</p>\r\n<h3><span id=\"Anh_sang_va_nhiet_do\">&Aacute;NH S&Aacute;NG V&Agrave; NHIỆT ĐỘ</span></h3>\r\n<p>C&acirc;y ưa th&iacute;ch nơi c&oacute; đầy đủ &aacute;nh nắng, m&ugrave;a h&egrave; kỵ &aacute;nh nắng gắt chiếu trực tiếp buổi trưa, th&iacute;ch kh&iacute; hậu ấm, kh&ocirc;ng chịu được nhiệt độ dưới 8&nbsp;độ c.</p>\r\n<h3><span id=\"Nuoc\">NƯỚC</span></h3>\r\n<p>Nước l&agrave; một trong những thứ kh&oacute; điều chỉnh với họ xương rồng, thường th&igrave; nếu để trong văn ph&ograve;ng th&igrave; bạn 1 tuần tưới 1 lần, c&ograve;n để ngoại thất th&igrave; tuần tưới 2 -3 lần t&ugrave;y theo thời tiết. Mỗi lần tưới ước lượng đủ để ẩm đất l&agrave; được.</p>\r\n<h3><span id=\"Nhan_giong\">NH&Acirc;N GIỐNG</span></h3>\r\n<p>Để&nbsp;<strong>nh&acirc;n giống xương rồng kim hổ</strong>&nbsp;bạn c&oacute; thể d&ugrave;ng hạt, t&aacute;ch c&aacute;c c&acirc;y con mọc tr&ecirc;n c&acirc;y rồi để tr&ecirc;n đất.</p>\r\n<h2><span id=\"Xuong_rong_kim_hoco_3_loai\">XƯƠNG RỒNG KIM HỔ&nbsp;C&Oacute; 3 LOẠI</span></h2>\r\n<p>Loại nhỏ đường k&iacute;nh khoảng 6 -8 cm: 30k</p>\r\n<p>Loại to khoảng 10 cm: 70k</p>\r\n<p>&nbsp;</p>\r\n<div class=\" d-flex mt-2\">\r\n<div class=\"fb-like fb_iframe_widget\" data-href=\"https://webcaycanh.com/xuong-rong-kim-ho/\" data-width=\"\" data-layout=\"button_count\" data-action=\"like\" data-size=\"small\" data-share=\"true\">&nbsp;</div>\r\n</div>\r\n</div>', 29000, '0', '8ac5d87a4d.jpg'),
(36, 'Xương Rồng Thần Long', 1, '<p>Xương rồng Thần Long l&agrave; một trong loại xương rồng c&oacute; hoa rất đẹp, hoa to v&agrave; c&oacute; nhiều m&agrave;u sắc kh&aacute;c nhau từ m&agrave;u trắng, cam, đỏ, hồng&hellip;C&acirc;y tr&ecirc;n 2 năm mới cho ra hoa. C&acirc;y mang &yacute; nghĩa cho sự bảo vệ, ph&ugrave; hợp để b&agrave;n l&agrave;m việc, b&agrave;n học, qu&aacute;n c&agrave; ph&ecirc;&hellip;</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2016/11/xuong-rong-than-long-5.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-5074\" src=\"https://webcaycanh.com/wp-content/uploads/2016/11/xuong-rong-than-long-5-300x271.jpg\" alt=\"xuong-rong-than-long-5\" width=\"300\" height=\"271\" /></a></p>\r\n<p><em>Xương rồng Thần Long</em></p>\r\n<h2><span id=\"Dac_diem_xuong_rong_Than_Long\">ĐẶC ĐIỂM XƯƠNG RỒNG THẦN LONG</span></h2>\r\n<p><strong><a href=\"https://webcaycanh.com/xuong-rong-than-long/\">Xương rồng Thần Long</a></strong>&nbsp;c&oacute; dạng h&igrave;nh cầu xung quanh chia th&agrave;nh nhiều mũi, tr&ecirc;n mỗi m&uacute;i đều c&oacute; gai nhỏ nhưng thưa, c&acirc;y c&oacute; m&agrave;u xanh đậm, thường ra hoa v&agrave;o cuối thu đầu đ&ocirc;ng, hoa chỉ được v&agrave;i ng&agrave;y nhưng mỗi lần ra đều cho ra nhiều b&ocirc;ng, sau khi t&agrave;n c&oacute; thể lại quả</p>\r\n<h2><span id=\"Cach_cham_soc_Xuong_rong_Than_Long\">C&Aacute;CH CHĂM S&Oacute;C XƯƠNG RỒNG THẦN LONG</span></h2>\r\n<h3><span id=\"Anh_sang\">&Aacute;NH S&Aacute;NG</span></h3>\r\n<p>Xương rồng Thần Long để ph&aacute;t triển tốt th&igrave; cần c&oacute; nhiều &aacute;nh s&aacute;ng, nếu để ở m&ocirc;i trường trong nh&agrave; th&igrave; c&acirc;y vẫn sống tuy nhi&ecirc;n sẽ kh&ocirc;ng cho ra hoa, v&igrave; thế c&acirc;y cần nhiều &aacute;nh s&aacute;ng, th&iacute;ch &aacute;nh s&aacute;ng buổi sớm v&agrave; chiều muộn</p>\r\n<h3><span id=\"Nuoc\">NƯỚC</span></h3>\r\n<p>Nước kh&ocirc;ng qu&aacute; quan trọng với lo&agrave;i&nbsp;<strong><a href=\"https://webcaycanh.com/xuong-rong-canh/\" rel=\"noopener\" target=\"_blank\">xương rồng</a></strong>, v&igrave; c&acirc;y c&oacute; khả năng trữ nước ở th&acirc;n, nghĩa l&agrave; chỉ cần cung cấp 1 lần th&igrave; c&acirc;y trữ nước trong th&acirc;n, c&oacute; thể nu&ocirc;i c&acirc;y cả th&aacute;ng, thường th&igrave; 1 tuần tưới nước một lần, hoặc khi n&agrave;o bạn thấy th&acirc;n n&oacute; nhẵn l&agrave; l&uacute;c n&oacute; đang thiếu nước th&igrave; khi đ&oacute; mới tưới để hạn chế được trường hợp tưới nhiều qu&aacute; m&agrave; c&acirc;y chết.</p>\r\n<p><span style=\"font-size: 1.17em;\">ĐẤT</span></p>\r\n<p>&nbsp;</p>\r\n<p>Đất với&nbsp;<em>xương rồng Thần Long</em>&nbsp;phải l&agrave; loại đất dễ tho&aacute;t nước, th&ocirc;ng tho&aacute;ng v&agrave; đủ dưỡng chất, bạn c&oacute; thể d&ugrave;ng đất c&oacute; trộn xỉ than đun rồi đập nhỏ, trộn với tro trấu, xơ dừa</p>\r\n<p>&nbsp;Nhiệt độ</p>\r\n<p>Trong tự nhi&ecirc;n hoang d&atilde;, c&acirc;y xương rồng v&agrave; c&acirc;y mọng nước c&oacute; thể tồn tại, chịu đựng trong khoảng nhiệt độ lớn, chừng 10&deg;C &ndash; 50&deg;C. Tuy nhi&ecirc;n, nhiệt độ th&iacute;ch hợp để c&acirc;y ph&aacute;t triển v&agrave;o khoảng 15&deg;C &ndash; 28&deg;C. Nhiệt độ qu&aacute; cao hoặc qu&aacute; thấp c&oacute; thể l&agrave;m cho c&acirc;y ngừng ph&aacute;t triển v&agrave; suy yếu.</p>\r\n<h2><span id=\"Xuong_rong_Than_Long_co_gia_70000_d_chua_bao_gom_ca_chau\">XƯƠNG RỒNG THẦN LONG C&Oacute; GI&Aacute; 70.000 Đ (CHƯA BAO GỒM CẢ CHẬU)</span></h2>\r\n<div class=\" d-flex mt-2\">\r\n<div class=\"fb-like fb_iframe_widget\" data-href=\"https://webcaycanh.com/xuong-rong-than-long/\" data-width=\"\" data-layout=\"button_count\" data-action=\"like\" data-size=\"small\" data-share=\"true\">&nbsp;</div>\r\n</div>', 69000, '1', 'ec1cd5e3a8.jpg'),
(37, 'Ông Bà', 4, '<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/ong-ba-phu-kien-terrarium-9x-garden-7.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/ong-ba-phu-kien-terrarium-9x-garden-6.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/ong-ba-phu-kien-terrarium-9x-garden-5.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/ong-ba-phu-kien-terrarium-9x-garden-1.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/ong-ba-phu-kien-terrarium-9x-garden-4.jpg\" alt=\"\" /></p>', 8000, '1', 'a81f73a5a4.jpg'),
(38, 'Toroto', 4, '<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/totoro-phu-kien-terrarium-9x-garden-4.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/totoro-phu-kien-terrarium-9x-garden-2.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/totoro-phu-kien-terrarium-9x-garden-3.jpg\" alt=\"\" /></p>', 13000, '0', '565fca66cb.jpg'),
(39, 'SEN ĐÁ HỒNG', 2, '<p>Sen đ&aacute; hồng l&agrave; một trong loại sen đ&aacute; nh&igrave;n rất th&iacute;ch mắt, c&acirc;y c&oacute; l&aacute; thưa, c&oacute; c&aacute;nh rất d&agrave;y, to v&agrave; cứng, c&acirc;y mang đến sức khỏe của người sở hữu. Ph&ugrave; hợp để b&agrave;n l&agrave;m việc, b&agrave;n học, trang tr&iacute; b&agrave;n c&agrave; ph&ecirc;, kh&aacute;ch sạn&hellip;<span>Sen đ&aacute; hồng&nbsp;&nbsp;</span>thường được trồng đơn hoặc 3 c&acirc;y 1 trong 1 chậu to, c&acirc;y ph&aacute;t triển theo chiều cao, trung b&igrave;nh c&oacute; chiều cao từ 5cm đến10 cm, khi c&acirc;y sinh trưởng v&agrave; ph&aacute;t triển c&oacute; c&aacute;c c&acirc;y con b&ecirc;n h&ocirc;ng mọc ph&aacute;t triển như c&acirc;y ch&iacute;nh th&agrave;nh từng bụi.</p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%91%C3%A1-h%E1%BB%93ng-2.jpg\"><img class=\"size-full wp-image-5107 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%91%C3%A1-h%E1%BB%93ng-2.jpg\" alt=\"sen-da-hong-2\" width=\"640\" height=\"480\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng-2.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng-2.jpg 640w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng-2-300x225.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng-2-600x450.jpg 600w\" data-lazy-sizes=\"(max-width: 640px) 100vw, 640px\" /></a></p>\r\n<p>Sen đ&aacute; hồng thuộc họ sen đ&aacute;, l&agrave; một trong những loại rất dễ chăm s&oacute;c v&agrave; c&oacute; sức ph&aacute;t triển m&atilde;nh liệt. Để cho c&acirc;y Sen đ&aacute; hồng đẹp th&igrave; bạn cần ch&uacute; &yacute; đến những điểm sau:</p>\r\n<p><span>1. &Aacute;nh s&aacute;ng</span></p>\r\n<p>&Aacute;nh s&aacute;ng quyết định m&agrave;u sắc của&nbsp;<span>Sen đ&aacute; hồng</span>&nbsp;v&agrave; đ&acirc;y cũng ch&iacute;nh l&agrave; b&iacute; quyết nếu bạn c&oacute; lỡ l&agrave;m mất lớp phấn tr&ecirc;n c&aacute;nh của Sen đ&aacute; hồng c&aacute;ch khắc phục l&agrave; bạn để c&acirc;y thật kh&ocirc; rồi tưới nhẹ nước sau đ&oacute; đem c&acirc;y phơi ra chỗ c&oacute; nắng v&agrave; gi&oacute;, để c&acirc;y trong 4 ng&agrave;y c&acirc;y sẽ tạo lại lớp phấn mới rất đẹp</p>\r\n<p><span>2. Nước:</span></p>\r\n<p><img class=\"wp-image-5108 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%91%C3%A1-h%E1%BB%93ng-3.png\" alt=\"sen-da-hong-3\" width=\"525\" height=\"550\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng-3.png\" /></p>\r\n<p>Sen đ&aacute; hồng l&agrave; loại mọng nước ch&uacute;ng kh&ocirc;ng cần qu&aacute; nhiều nước vẫn c&oacute; thể sống tốt.</p>\r\n<p>Tuyệt đối kh&ocirc;ng tưới nước mạnh l&ecirc;n c&acirc;y điều n&agrave;y sẽ l&agrave;m mất lớp phấn đ&oacute;</p>\r\n<p>Phương ph&aacute;p tưới tốt nhất n&ecirc;n &aacute;p dụng cho Sen đ&aacute; hồng l&agrave; bạn n&ecirc;n tưới nước theo kiểu ngấm ngược.</p>\r\n<p><span>3.Ph&acirc;n b&oacute;n</span></p>\r\n<p><img class=\" wp-image-5109 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-%C4%91%C3%A1-h%E1%BB%93ng.jpg\" alt=\"sen-da-hong\" width=\"567\" height=\"425\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng.jpg 640w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng-300x225.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/Sen-đ&aacute;-hồng-600x450.jpg 600w\" data-lazy-sizes=\"(max-width: 567px) 100vw, 567px\" /></p>\r\n<p>Để c&acirc;y sinh trưởng v&agrave; ph&aacute;t triển tốt, bạn n&ecirc;n b&oacute;n th&ecirc;m c&aacute;c loại ph&acirc;n b&oacute;n l&aacute;, ph&acirc;n b&ograve; h&agrave;ng th&aacute;ng, mỗi năm n&ecirc;n thay đất cho c&acirc;y từ 1-2 lần.</p>\r\n<p><span>4. Đất</span></p>\r\n<p>Sen đ&aacute; hồng cần đất c&oacute; thể kh&ocirc; nhanh ch&oacute;ng v&agrave; dễ tho&aacute;t nước để c&oacute; thể gi&uacute;p c&acirc;y kh&ocirc;ng bị thối rễ do qu&aacute; ẩm ướt bạn trộn loại đất đ&aacute; ri&ecirc;ng. Tuy nhi&ecirc;n với loại đất trồng chất lượng tốt sẽ tốt hơn. Bạn cần nhớ, khi nắm v&agrave;o tay một nắm đất trộn, n&oacute; cần vụn n&aacute;t ra v&agrave; c&aacute;c th&agrave;nh phần rời ra, chứ kh&ocirc;ng d&iacute;nh bết khi bạn đ&atilde; bu&ocirc;ng tay ra, đơn giản l&agrave; loại đất n&agrave;y cần kh&ocirc; hạn nhanh bạn h&atilde;y cho c&aacute;t thật v&agrave;o, nếu thay chậu th&igrave; bạn h&atilde;y&nbsp;thay đất cho c&acirc;y&nbsp;<span>Sen đ&aacute; hồng</span>&nbsp; sẽ gi&uacute;p cho ch&uacute;ng khoẻ mạnh.</p>\r\n<div>&nbsp;</div>\r\n\r\n<div class=\"fb-comments fb_iframe_widget fb_iframe_widget_fluid_desktop\" data-href=\"https://cayvahoa.net/sen-da-hong/\" data-numposts=\"20\" data-colorscheme=\"light\">&nbsp;</div>', 40000, '0', '60dda48d53.jpg'),
(40, 'SEN ĐÁ SEDUM DẠ QUANG', 2, '<p>Sen đ&aacute; được biết đến l&agrave; loại c&acirc;y cảnh để b&agrave;n được ưa chuộng nhiều nhất, trong đ&oacute; loại sedum dạ quang được nhiều người chơi c&acirc;y cảnh ưa chuộng. Sen đ&aacute; sedum dạ quang thường được d&ugrave;ng để b&agrave;n, trang tr&iacute; c&aacute;c nơi trong căn nh&agrave;, qu&aacute;n c&agrave; ph&ecirc;,&hellip;</p>\r\n<p>Sedum dạ quang c&oacute; nguồn gốc từ d&ograve;ng sedum, họ sen đ&aacute;, sedum l&agrave; d&ograve;ng bao gồm những lo&agrave;i c&acirc;y c&oacute; h&igrave;nh d&aacute;ng nhỏ b&eacute; v&agrave; đ&aacute;ng y&ecirc;u.&nbsp;<span>Sen đ&aacute; sedum dạ quang</span>&nbsp;tượng trưng cho một t&igrave;nh y&ecirc;u, t&igrave;nh bạn vĩnh cửu kh&ocirc;ng bao giờ ththay đổi d&ugrave; c&oacute; kh&oacute; khăn, vất vả như m&agrave;n đ&ecirc;m bao phủ mờ mịt th&igrave; vẫn c&oacute; người dẫn lỗi để c&oacute; thể dễ d&agrave;ng vượt qua.</p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-sedum-d%E1%BA%A1-quang-2.jpg\"><img class=\" wp-image-4960 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-sedum-d%E1%BA%A1-quang-2.jpg\" alt=\"sen-da-sedum-da-quang-2\" width=\"519\" height=\"389\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-2.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-2.jpg 800w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-2-300x225.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-2-768x576.jpg 768w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-2-600x450.jpg 600w\" data-lazy-sizes=\"(max-width: 519px) 100vw, 519px\" /></a></p>\r\n<p>C&acirc;y sedum dạ quang mọc th&agrave;nh những bụi nhỏ c&oacute; h&igrave;nh d&aacute;ng dạng đ&agrave;i giống như đ&agrave;i sen, m&agrave;u sắc rất bắt mắt, s&aacute;ng rực rất đẹp. Ch&iacute;nh v&igrave; điều n&agrave;y, n&ecirc;n loại c&acirc;y mới c&oacute; t&ecirc;n gọi l&agrave; sedum dạ quang.&nbsp;C&aacute;ch chăm s&oacute;c Sen đ&aacute; sedum dạ quang v&ocirc; c&ugrave;ng đơn giản v&agrave; dễ d&agrave;ng. Điều cần ch&uacute; &yacute; v&agrave; quan trọng nhất l&agrave; &aacute;nh s&aacute;ng v&agrave; nước.</p>\r\n<p><span>1. Nước</span></p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-sedum-d%E1%BA%A1-quang-3.jpg\"><img class=\"size-full wp-image-4961 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-sedum-d%E1%BA%A1-quang-3.jpg\" alt=\"sen-da-sedum-da-quang-3\" width=\"480\" height=\"480\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-3.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-3.jpg 480w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-3-150x150.jpg 150w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-3-300x300.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-3-180x180.jpg 180w\" data-lazy-sizes=\"(max-width: 480px) 100vw, 480px\" /></a><br />-C&acirc;y c&oacute; khả năng giữ nước ở l&aacute; v&agrave; th&acirc;n, n&ecirc;n kh&ocirc;ng cần tưới nước nhiều.<br />&ndash; Mật độ tưới nước l&agrave; một tuần tưới 1 lần. Với thời tiết kh&ocirc; n&oacute;ng th&igrave; tưới 2 lần.<br />&ndash; Hạn chế tuyệt đối tưới trực tiếp n&ecirc;n l&aacute; bởi v&igrave; sen đ&aacute; sedum dạ quang c&oacute; dạng đ&agrave;i n&ecirc;n nước đọng tr&ecirc;n l&aacute; qu&aacute; l&acirc;u sẽ l&agrave;m thối l&aacute;.</p>\r\n<p><span>2. Đất trồng</span><br />&ndash; Loại đấy th&iacute;ch hợp l&agrave;: đất dễ tho&aacute;t nước v&agrave; c&oacute; độ m&ugrave;n cao<br />&ndash; Trộn hỗn hợp đất trồng với sơ dừa + tro + xỉ than. Sau đ&oacute; đập nhỏ trộn th&ecirc;m ph&acirc;n b&ograve; kh&ocirc; hoặc một số thuốc chống nấm mốc sẽ tạo th&agrave;nh một loại đất tốt rất th&iacute;ch hợp với trồng sen đ&aacute;.</p>\r\n<p><span>3. &Aacute;nh s&aacute;ng.</span></p>\r\n<p><a href=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-sedum-d%E1%BA%A1-quang.jpg\"><img class=\" wp-image-4962 aligncenter     lazyloaded\" src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-%C4%91%C3%A1-sedum-d%E1%BA%A1-quang.jpg\" alt=\"sen-da-sedum-da-quang\" width=\"586\" height=\"586\" data-lazy-src=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang.jpg\" data-lazy-srcset=\"https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang.jpg 639w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-150x150.jpg 150w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-300x300.jpg 300w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-600x600.jpg 600w, https://cayvahoa.net/wp-content/uploads/2016/11/sen-đ&aacute;-sedum-dạ-quang-180x180.jpg 180w\" data-lazy-sizes=\"(max-width: 586px) 100vw, 586px\" /></a><br />&ndash; L&agrave; lo&agrave;i c&acirc;y ưa s&aacute;ng nhưng kh&ocirc;ng th&iacute;ch hợp &aacute;nh s&aacute;ng qu&aacute; gắt v&agrave; nắng.<br />&ndash; Che 30% &aacute;nh s&aacute;ng bằng lưới đen khi c&oacute; &aacute;nh nắng buổi trưa.<br />&ndash; &Aacute;nh s&aacute;ng buổi sớm v&agrave; xế chiều l&agrave; th&iacute;ch hợp nhất cho c&acirc;y.</p>\r\n<p><span>4. Nh&acirc;n giống</span><br />C&oacute; 2 phương ph&aacute;p nh&acirc;n giống:<br />&ndash; Sử dụng phương ph&aacute;p t&aacute;ch nhỏ ra trồng v&agrave;o khu đất rộng để nh&acirc;n giống, phương ph&aacute;p nh&acirc;n giống n&agrave;y an to&agrave;n v&agrave; c&oacute; tỷ lệ th&agrave;nh c&ocirc;ng cao.<br />&ndash; Sử dụng phương ph&aacute;p nh&acirc;n giống&nbsp;<span>sen đ&aacute; sedum dạ quang</span>&nbsp;bằng c&aacute;ch gi&acirc;m c&agrave;nh.</p>\r\n<div>&nbsp;</div>\r\n\r\n<div class=\"fb-comments fb_iframe_widget fb_iframe_widget_fluid_desktop\" data-href=\"https://cayvahoa.net/sen-da-sedum-da-quang/\" data-numposts=\"20\" data-colorscheme=\"light\">&nbsp;</div>', 50000, '0', 'b24c48d82a.jpg'),
(41, 'Cây Xương rồng Thanh Sơn', 1, '<p>Xương rồng Thanh Sơn hay c&ograve;n gọi l&agrave; xương rồng Ngọn N&uacute;i. C&acirc;y thuộc&nbsp;họ Cactaceae c&oacute; từ 24 đến 220 chi, t&ugrave;y theo nguồn (90 chi phổ biến nhất), trong đ&oacute; c&oacute; từ 1.500 đến 1.800 lo&agrave;i. Những c&acirc;y xương rồng được biết đến như l&agrave; c&oacute; nguồn gốc từ ch&acirc;u Mỹ, nhất l&agrave; ở những v&ugrave;ng sa mạc. C&oacute; lẽ ch&iacute;nh v&igrave; sức sống m&atilde;nh liệt của n&oacute;, m&agrave; xương rồng Thanh Sơn được nhiều bạn trẻ ưa th&iacute;ch, mua về trang tr&iacute; b&agrave;n học, b&agrave;n l&agrave;m việc, tặng nhau trong c&aacute;c dịp lễ như mong muốn d&ugrave; c&oacute; kh&oacute; khăn thế n&agrave;o cũng vẫn c&oacute; thể vượt qua.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2016/08/xuong-rong-thanh-son-3.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4978\" src=\"https://webcaycanh.com/wp-content/uploads/2016/08/xuong-rong-thanh-son-3-224x300.jpg\" alt=\"xuong-rong-thanh-son-3\" width=\"224\" height=\"300\" /></a></p>\r\n<p>Xương rồng Thanh Sơn</p>\r\n<h2><span id=\"Dac_diem_va_cach_cham_soc_xuong_rong_Thanh_Son\">ĐẶC ĐIỂM V&Agrave; C&Aacute;CH CHĂM S&Oacute;C XƯƠNG RỒNG THANH SƠN</span></h2>\r\n<p>Xương rồng Thanh Sơn hay c&oacute; t&ecirc;n gọi l&agrave; xương rồng Ngọn N&uacute;i c&oacute; đặc điểm v&agrave; sự ph&aacute;t triển như những ngọn n&uacute;i thu nhỏ tr&ugrave;ng tr&ugrave;ng điệp điệp, gồm 1 th&acirc;n ch&iacute;nh sau c&acirc;y mọc th&agrave;nh nhiều nh&aacute;nh nhỏ tr&ecirc;n gốc ch&iacute;nh, ở điều kiện th&iacute;ch hợp c&acirc;y ph&aacute;t triển rất nhanh, một nh&aacute;nh nhỏ của c&acirc;y thường c&oacute; 5 kh&iacute;a&nbsp;chạy dọc từ gốc cho đến ngọn, tr&ecirc;n kh&iacute;a c&oacute; những gai nhỏ mền.</p>\r\n<h2><span id=\"Cach_cham_soc_xuong_rong_Thanh_Son\">C&Aacute;CH CHĂM S&Oacute;C XƯƠNG RỒNG THANH SƠN</span></h2>\r\n<p>Xương rồng Thanh Sơn chăm s&oacute;c rất dễ v&agrave; đơn giản, c&acirc;y kh&ocirc;ng đ&ograve;i hỏi đất c&oacute; độ dinh dương cao, hay nhiều &aacute;nh s&aacute;ng, nước, kh&ocirc;ng kh&iacute;&hellip;Bạn chỉ cần lưu &yacute; tưới &iacute;t nước v&agrave; để c&acirc;y kh&ocirc;ng bị &uacute;ng l&agrave; c&acirc;y đ&atilde; c&oacute; thể ph&aacute;t triển.</p>\r\n<p><img class=\"aligncenter size-medium wp-image-4976\" src=\"https://webcaycanh.com/wp-content/uploads/2016/08/xuong-rong-thanh-son-1-300x219.jpg\" alt=\"xuong-rong-thanh-son-1\" width=\"300\" height=\"219\" /></p>\r\n<h3><span id=\"Anh_sang\">&Aacute;NH S&Aacute;NG</span></h3>\r\n<p>C&acirc;y c&oacute; thể chịu được &aacute;nh nắng gắt tuy nhi&ecirc;n trời m&ugrave;a h&egrave; th&igrave; n&ecirc;n l&agrave;m bạt che bớt nắng cho c&acirc;y, v&igrave; ng&agrave;y h&egrave; &aacute;nh nắng k&egrave;m theo nhiệt độ rất cao hoặc trời buổi trưa th&igrave; bạn mang c&acirc;y v&agrave;o nh&agrave;. Nếu xương rồng Thanh Sơn được chọn l&agrave;m c&acirc;y để b&agrave;n trong nh&agrave; hoặc văn ph&ograve;ng th&igrave; 1 tuần bạn n&ecirc;n mang ra nắng &nbsp;2 lần để c&acirc;y c&oacute; thể sống khỏe.</p>\r\n<h3><span id=\"Nuoc\">NƯỚC</span></h3>\r\n<p>Một trong những yếu tố khiến nhiều xương rồng phải ra đi đ&oacute; ch&iacute;nh l&agrave; nước, người chăm xương rồng lu&ocirc;n lo lắng c&acirc;y kh&ocirc;ng đủ nước sẽ ph&aacute;t triển chậm, n&ecirc;n tưới nước đều đặn ng&agrave;y n&agrave;o cũng tưới v&agrave; một ng&agrave;y đẹp trời c&acirc;y nhũn v&agrave; rời xa chủ nh&acirc;n, c&aacute;c bạn h&atilde;y nhớ xương rồng c&oacute; khả năng trữ nước ở th&acirc;n n&ecirc;n bạn tưới 1 lần th&igrave; n&oacute; đ&atilde; h&uacute;t đủ v&agrave; khi n&agrave;o c&acirc;y c&oacute; hiện tượng mềm, nhăn th&igrave; l&uacute;c đ&oacute; mới l&agrave; thiếu nước. Tưới nước nhiều khiến đất l&uacute;c n&agrave;o cũng ẩm l&agrave; điều kiện l&yacute; tưởng cho vi khuẩn ph&aacute;t triển dẫn đến n&ecirc;n c&acirc;y rất dễ chết.</p>\r\n<p><a href=\"https://webcaycanh.com/wp-content/uploads/2016/08/xuong-rong-thanh-son.jpg\" data-fancybox=\"gallery\"><img class=\"aligncenter size-medium wp-image-4975\" src=\"https://webcaycanh.com/wp-content/uploads/2016/08/xuong-rong-thanh-son-224x300.jpg\" alt=\"xuong-rong-thanh-son\" width=\"224\" height=\"300\" /></a></p>\r\n<p>C&acirc;y xương rồng Thanh Sơn nếu để trong nh&agrave;, văn ph&ograve;ng điều kiện m&aacute;t kh&ocirc;ng n&oacute;ng th&igrave; thường 1 tuần tưới 1 lần, c&ograve;n để ngo&agrave;i trời hoặc m&ocirc;i trường n&oacute;ng đất nhanh mất nước th&igrave; 1 tuần tưới 2 lần. Khi tưới n&ecirc;n tưới ở gốc tr&aacute;nh tưới l&ecirc;n c&acirc;y v&igrave; c&acirc;y c&oacute; nhiều kẽ khiến nước c&oacute; thể bị đọng ở đ&oacute; l&acirc;u ng&agrave;y m&agrave; kh&ocirc;ng tho&aacute;t được</p>\r\n<h3><span id=\"Dat_trong\">ĐẤT TRỒNG</span></h3>\r\n<p>Loại đất ưa th&iacute;ch với xương rồng Thanh Sơn l&agrave; loại c&oacute; khả năng tho&aacute;t nước tốt độ m&ugrave;n cao, bạn c&oacute; thể d&ugrave;ng xỉ than, than tổ ong đun rồi đập nhỏ sau đ&oacute; lọc hết vụn mụn ra rồi trộn với tro, chấu, đất trồng, ph&acirc;n b&ograve; kh&ocirc;, xơ dừa&hellip; để đất th&ocirc;ng tho&aacute;ng m&agrave; vẫn c&oacute; đủ dưỡng chất để c&acirc;y ph&aacute;t triển nhanh</p>\r\n<h3><span id=\"Nhan_giong\">NH&Acirc;N GIỐNG</span></h3>\r\n<p>C&acirc;y c&oacute; thể nh&acirc;n giống bằng nhiều c&aacute;ch c&oacute;&nbsp;thể t&aacute;ch nh&aacute;nh, gi&acirc;m hoặc trồng bằng hạt đều được</p>\r\n<h2><span id=\"Xuong_rong_Thanh_Son_co_gia_39000_VND\">XƯƠNG RỒNG THANH SƠN C&Oacute; GI&Aacute;:&nbsp;<span>39.000 VNĐ</span></span></h2>\r\n<div class=\" d-flex mt-2\">\r\n<div class=\"fb-like fb_iframe_widget\" data-href=\"https://webcaycanh.com/xuong-rong-thanh-son/\" data-width=\"\" data-layout=\"button_count\" data-action=\"like\" data-size=\"small\" data-share=\"true\">&nbsp;</div>\r\n</div>', 39000, '0', '62c84f6cd0.jpg'),
(43, 'Nhà Gỗ', 4, '<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/nha-go-phu-kien-terrarium-9x-garden-1.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/nha-go-phu-kien-terrarium-9x-garden-9.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/nha-go-phu-kien-terrarium-9x-garden-8.jpg\" alt=\"\" /></p>\r\n<p><img src=\"https://9xgarden.com/wp-content/uploads/2020/04/nha-go-phu-kien-terrarium-9x-garden-7.jpg\" alt=\"\" /></p>', 15000, '0', '412a4a4a61.jpg');

